// (c) Kallol Borah, 2020
// Implementation of a Verified account holder

pragma solidity 0.5.7;

import "../interfaces/VerifiedSystem.sol";
import "../interfaces/VerifiedLedger.sol";
import "../interfaces/VerifiedHolder.sol";
import "abdk-libraries-solidity/ABDKMathQuad.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";


contract Holder is Initializable, VerifiedHolder{

    using ABDKMathQuad for uint256;
    using ABDKMathQuad for int256;
    using ABDKMathQuad for bytes16;

    VerifiedSystem private factory;

    bytes32 holderName; 

    address accountCreator;

    address accountHolder;

    //references to ledgers with this account holder
    address[] ledgers;
    address ledger;   

    struct entry{
        bytes32 ledgerName;
        bytes32 ledgerGroup;
        bytes16 ledgerBalance;
    }

    mapping(address => mapping (uint256 => entry)) statement;

    //initiliaze proxies
    function initialize(bytes32 _holderName, address _accountCreator, address _accountHolder, address _owner) public initializer{
        // this account holder can be referred from this account
        factory = VerifiedSystem(_owner);
        holderName = _holderName;
        accountCreator = _accountCreator;
        accountHolder = _accountHolder;
    }

    function updateAccountStatement() external{
        require(accountCreator==msg.sender);
        // get ledgers for this account holder
        for(uint256 i=0; i<factory.getNumber("ledgers"); i++){
            ledger = factory.getLedger(accountCreator,i);
            if(ledger!=address(0x0))
                ledgers.push(ledger);
        }
        for(uint256 i=0; i<ledgers.length; i++){
            bytes16 balance;
            bytes32 name;
            bytes32 group;
            (balance, name, group) = VerifiedLedger(ledgers[i]).getBalance(); 
            bool found = false;
            uint256 p = 0;   
            while(!found && statement[msg.sender][p].ledgerName!=""){
                if(statement[msg.sender][p].ledgerName==name && statement[msg.sender][p].ledgerGroup==group){
                    found = true;
                    statement[msg.sender][p].ledgerBalance = balance;
                }
                p=p+1;
            }        
            if(!found){
                statement[msg.sender][p].ledgerName = name;
                statement[msg.sender][p].ledgerGroup = group;
                statement[msg.sender][p].ledgerBalance = balance;
            }
        }
    }

    function getAccountStatement(uint256 statementIndex) external view returns(bytes32, bytes32, bytes16){
        require(accountCreator==msg.sender);
        return(statement[msg.sender][statementIndex].ledgerName, statement[msg.sender][statementIndex].ledgerGroup, statement[msg.sender][statementIndex].ledgerBalance);
    }

    function createLedger(bytes32 _ledgerName, bytes32 _ledgerGroup) external{
        require(accountCreator==msg.sender);
        factory.createLedger(_ledgerName, _ledgerGroup, accountCreator, accountHolder);
    }

}
