// Verified Liquidity token 
// (c) Kallol Borah, 2021

//"SPDX-License-Identifier: UNLICENSED"

pragma solidity 0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

import "./interfaces/IMarketMaker.sol";

contract LPToken is ERC20, Ownable, Pausable {

    using SafeMath for uint256;

    address[] private Platforms;
    address[] private Tokens;
    
    address[] private LPs;
    mapping(address => uint256) private liquidityProvider; 

    uint256 investmentCap=0;
    uint256 timeLimit=0;
    
    uint256 totalStaked=0;
    uint256 totalLiquidity=0;
    uint256 totalIssued=0;

    address payable verified;

    struct investment{
        uint256 tokenAmount;
        address tokenAddress;
    }

    struct liquidity{
        uint256 issued;
        uint256 staked;
    }

    struct performance{
        uint256 liquidityProvided;
        uint256 commissionsEarned;
    }

    // mapping investor to invested tokens
    mapping(address => mapping(string => investment)) private investors;
    // mapping investor to liquidity tokens issued
    mapping(address => liquidity) private stakes; 
    // mapping platform to performance of liquidity provided
    mapping(address => performance) private roi;

    event RequestIssue(address investor, string tokenName, uint256 tokenAmount);
    
    /**
        Constructor for the Verified Liquidity token
        @param  _supply total supply of VITTA
        @param  _cap    investment limit for each investor
        @param  _limit  time limit for issue close
     */
    constructor(uint256 _supply, uint256 _cap, uint256 _limit) 
        ERC20("Verified", "VITTA"){
        _mint(msg.sender, _supply);
        verified = payable(msg.sender);
        investmentCap = _cap;
        timeLimit = _limit;
        totalIssued = 0;
    }

    /**
        Creates supply of the Verified Liquidity token after initial issue with constructor defined parameters
        @param  _supply total supply of VITTA
        @param  _cap    investment limit for each investor
        @param  _limit  time limit for issue close 
     */
    function createSupply(uint256 _supply, uint256 _cap, uint256 _limit) external {
        require(msg.sender==owner());
        _mint(msg.sender, _supply);
        investmentCap = SafeMath.add(investmentCap, _cap);
        timeLimit = _limit;
    }

    /**
        Specifies list of supported tokens that can be invested in the Verified Liquidity token
        @param  _tokens array of supported token addresses
     */
    function supportTokens(address[] memory _tokens) external {
        require(msg.sender==owner());
        Tokens = _tokens;
    }

    /**
        Checks if a specified token is supported for investing in the Verified Liquidity token
        @param  _token  token that is supported for investment
     */
    function checkSupportForToken(address _token) external view returns(bool){
        bool found = false;
        for(uint i=0; i<Tokens.length; i++){
            if(Tokens[i]==_token)
                found = true;
        }
        return found;
    }

    /**
        Registers a liquidity platform (eg, DEX) where Verified Liquidity tokens can be used to underwrite investments
        @param  _liquidityPlatform  address of the market making platform
     */
    function registerPlatform(address _liquidityPlatform) external {
        require(owner()==msg.sender);
        bool found = false;
        for(uint i=0; i<Platforms.length; i++){
            if(Platforms[i]==_liquidityPlatform)
                found = true;
        }
        if(!found)
            Platforms.push(_liquidityPlatform);
    }

    /**
        Receiver of ether invested in the Verified Liquidity token
     */
    receive() external payable{
        if(timeLimit!=0)
            require(block.timestamp > timeLimit);
        //ether paid in
        require(msg.value !=0);
        //contract must not be paused
        require(paused() == false);
        //issue liquidity token
        emit RequestIssue(msg.sender, "ether", msg.value);
        investors[msg.sender]["ether"].tokenAmount = SafeMath.add(investors[msg.sender]["ether"].tokenAmount, msg.value);
        if(liquidityProvider[msg.sender]==0){
            LPs.push(msg.sender);
            liquidityProvider[msg.sender]=LPs.length;
        }
    }

    /**
        Used by external apps (eg, exchange, wallet) to buy Verified Liquidity token 
        @param  _token  address of token used by investor to buy the VITTA
        @param  _amount amount of token that is transferred from investor to this VITTA issuing contract's account for the investor
     */
    function buy(address _token, uint256 _amount) external {
        if(timeLimit!=0)
            require(block.timestamp > timeLimit);
        require(paused() == false);
        bool found = false;
        for(uint i=0; i<Tokens.length; i++){
            if(Tokens[i]==_token)
                found = true;
        }
        if(found){
            if(IERC20(_token).transferFrom(msg.sender, address(this), _amount)){
                emit RequestIssue(msg.sender, ERC20(_token).name(), _amount);
                investors[msg.sender][ERC20(_token).name()].tokenAmount = SafeMath.add(investors[msg.sender][ERC20(_token).name()].tokenAmount, _amount);
                investors[msg.sender][ERC20(_token).name()].tokenAddress = _token;
                if(liquidityProvider[msg.sender]==0){
                    LPs.push(msg.sender);
                    liquidityProvider[msg.sender]=LPs.length;
                }
            }
        }
    }

    /**
        Fetches investors in VITTA
     */
    function getInvestors() external view returns(address[] memory){
        require(msg.sender==owner());
        return LPs;
    }

    /**
        Fetches investment detail for specific investor in VITTA
        @param  _investor   address of investor
        @param  _tokenName  name of token invested by investor
     */
    function getInvestment(address _investor, string memory _tokenName) external view returns(uint256){
        require(msg.sender==owner());
        return (investors[_investor][_tokenName].tokenAmount);
    }

    /**
        Used by Issuers (eg, asset managers) to issue VITTA to investors or refund paid in tokens to investors if investment cap is breached
        @param  _investor       address of investor
        @param  _tokenName      name of token invested in VITTA
        @param  _tokenAmount    amount of token invested in VITTA
        @param  _LPToIssue      amount of VITTA to issue to investor
     */
    function issue(address _investor, string memory _tokenName, uint256 _tokenAmount, uint256 _LPToIssue) external {
        require(msg.sender == owner());
        //If investment is outside limits, refund tokens invested back to investor
        if((_LPToIssue > investmentCap) && (_LPToIssue > SafeMath.sub(totalSupply(), totalIssued))){
            investors[_investor][_tokenName].tokenAmount = SafeMath.sub(investors[_investor][_tokenName].tokenAmount, _tokenAmount);
            IERC20(investors[_investor][_tokenName].tokenAddress).transferFrom(address(this), _investor, _tokenAmount);
        }
        //else issue VITTA to investor and transfer managed liquidity to deployer that allocates it to issuer
        else{
            transferFrom(owner(), _investor, _LPToIssue);  
            totalIssued = SafeMath.add(totalIssued, _LPToIssue);    
            stakes[_investor].issued = SafeMath.add(stakes[_investor].issued, _LPToIssue);
            if(keccak256(abi.encodePacked(_tokenName))!="ether")
                IERC20(investors[_investor][_tokenName].tokenAddress).transferFrom(address(this), owner(), _tokenAmount);
            else
                (verified).transfer(_tokenAmount); 
        }
    }

    /**
        Used by VITTA holder to stake it for providing liquidity for underwriting investments
        @param  _toStake    amount of VITTA to stake
     */
    function stake(uint256 _toStake) external {
        require(paused() == false);
        require(stakes[msg.sender].issued >= _toStake);
        if(transferFrom(msg.sender, address(this), _toStake)){
            totalStaked = SafeMath.add(totalStaked, _toStake);
            stakes[msg.sender].issued = SafeMath.sub(stakes[msg.sender].issued, _toStake);
            stakes[msg.sender].staked = SafeMath.add(stakes[msg.sender].staked, _toStake);
        }
    }

    /**
        Used by VITTA holder to withdraw staked liquidity for underwriting investments
        @param  _fromStake    amount of VITTA staked
     */
    function withdraw(uint256 _fromStake) external {
        require(paused() == false);
        require(stakes[msg.sender].staked >= _fromStake);
        if(SafeMath.sub(totalStaked, totalLiquidity) >= _fromStake){
            if(transferFrom(address(this), msg.sender, _fromStake)){
                totalStaked = SafeMath.sub(totalStaked, _fromStake);
                stakes[msg.sender].staked = SafeMath.sub(stakes[msg.sender].staked, _fromStake);
            }
        }
    }

    /**
        Pay out of income by Market maker contracts to VITTA Liquidity providers
        @param  _distribution   amount of VITTA to mint and distribute pro rata to liquidity providers
     */
    function distribute(uint256 _distribution) external {
        require(paused() == false);
        bool found = false;
        for(uint i=0; i<Platforms.length; i++){
            if(Platforms[i]==msg.sender)
                found = true;
        }
        if(found && totalStaked!=0){
            _mint(owner(), _distribution);
            roi[msg.sender].commissionsEarned = SafeMath.add(roi[msg.sender].commissionsEarned, _distribution);
            for(uint i=0; i<LPs.length; i++){
                uint256 prorataStake = SafeMath.div(stakes[LPs[i]].staked, totalStaked);
                transferFrom(owner(), LPs[i], SafeMath.mul(prorataStake, _distribution));                
            }
        }
    }

    /**
        Provides staked VITTA to Market maker contracts based on prior liquidity consumed and commission earned 
     */
    function provideLiquidity() external {
        require(paused() == false);
        require(msg.sender == owner());
        uint256 totalLiquidityConsumed = 0;
        uint256 totalCommissionsEarned = 0;
        uint256 unStakedLiquidity = SafeMath.sub(totalStaked, totalLiquidity);
        for(uint i=0; i<Platforms.length; i++){
            uint256 liquidityBalance = SafeMath.sub(roi[Platforms[i]].liquidityProvided, balanceOf(Platforms[i]));
            totalLiquidityConsumed = SafeMath.add(liquidityBalance, totalLiquidityConsumed);
            totalCommissionsEarned = SafeMath.add(roi[Platforms[i]].commissionsEarned, totalCommissionsEarned);
        }
        for(uint j=0; j<Platforms.length; j++){
            uint256 liquidityBalance = SafeMath.sub(roi[Platforms[j]].liquidityProvided, balanceOf(Platforms[j]));
            uint256 totalScore = SafeMath.add(liquidityBalance, roi[Platforms[j]].commissionsEarned);
            uint256 provision = SafeMath.mul(SafeMath.div(totalScore, SafeMath.add(totalLiquidityConsumed, totalCommissionsEarned)),unStakedLiquidity);
            IMarketMaker(Platforms[j]).stake(provision);
            transferFrom(address(this), Platforms[j], provision);
            totalLiquidity = SafeMath.add(totalLiquidity, provision);
            roi[Platforms[j]].liquidityProvided = SafeMath.add(roi[Platforms[j]].liquidityProvided, provision);
        }
    }

    /**
        Pauses this contract
     */
    function pause() public {
        require(msg.sender == owner());
        _pause();
    }
    
    /**
        Unpauses this contract
     */
    function unpause() public {
        require(msg.sender == owner());
        _unpause();
    }

}

