// (c) Kallol Borah, 2020
// Implementation of the Verified accounts factory

pragma solidity 0.5.7;

import "../interfaces/VerifiedHolder.sol";
import "../interfaces/VerifiedLedger.sol";
import "../interfaces/VerifiedAccount.sol";
import "../interfaces/VerifiedClient.sol";
import "../interfaces/VerifiedSystem.sol";
import "../interfaces/ViaFactory.sol";
import "@openzeppelin/upgrades/contracts/upgradeability/ProxyFactory.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";

contract AccountFactory is VerifiedSystem, ProxyFactory, Initializable {

    // first, a client creates an account holder representing an account statement
    struct accountholder{
        bytes32 holderName;
        address accountHolder;
    }

    address[] public accountHolderList;

    // then, a ledger is created in an account statement (ie, for an account holder)
    struct ledger{
        bytes32 ledgerName;
        bytes32 group;
        address ledger;
    }

    address[] public ledgerList;

    // then, an account is created in a ledger
    struct account{
        bytes32 accountName;
        address account;
        bytes32 currency;
    }

    address[] public accountList;

    // mapping address of account holders to a ledger they have (identified by ledger address)
    mapping(address => mapping(address => ledger)) public ledgers;

    // mapping address of ledger to an account in it (identified by account address)
    mapping(address => mapping(address => account)) public accounts;

    // mapping address of client to an account holder (identified by its account holder address)
    mapping(address => mapping(address => accountholder)) public accountholders;

    //notifying listeners
    event LedgerCreated(address indexed ledger, bytes32 ledgerName, bytes32 group, address accountHolder);
    event AccountCreated(address indexed account, bytes32 accountName, address ledger);
    event HolderCreated(address indexed holder, bytes32 holderName, address accountCreator);

    //references to contract
    VerifiedClient private clientRef;
    VerifiedAccount private accountRef;
    VerifiedHolder private holderRef;
    VerifiedLedger private ledgerRef;
    ViaFactory private via;

    //addresses fo contracts
    address accountAddress;
    address holderAddress;
    address ledgerAddress;

    //owner is someone who can upgrade this contract
    function initialize(address _ledger, address _account, address _holder, address _client, address _viaFactory) public {
        clientRef = VerifiedClient(_client);
        accountRef = VerifiedAccount(_account);
        holderRef = VerifiedHolder(_holder);
        ledgerRef = VerifiedLedger(_ledger);
        accountAddress = _account;
        holderAddress = _holder;
        ledgerAddress = _ledger;
        via = ViaFactory(_viaFactory);
    }

    // returns number of accounts or number of ledgers
    function getNumber(bytes32 what) external view returns(uint256){
        require(msg.sender==holderAddress || msg.sender==accountAddress || msg.sender==ledgerAddress);
        if(what=="accounts")
            return accountList.length;
        else if(what=="ledgers")
            return ledgerList.length;
    }

    // return account address in ledger by account index
    function getAccount(address _ledger, uint256 _index) external view returns(address){
        require(msg.sender==holderAddress || msg.sender==accountAddress || msg.sender==ledgerAddress);
        if(accounts[_ledger][accountList[_index]].account!=address(0x0))
            return accounts[_ledger][accountList[_index]].account;
        else
            return address(0x0);
    }

    // return address of ledger for its account holder by an index
    function getLedger(address _accountHolder, uint256 _index) external view returns(address){
        require(msg.sender==holderAddress || msg.sender==accountAddress || msg.sender==ledgerAddress);
        if(ledgers[_accountHolder][ledgerList[_index]].ledger!=address(0x0))
            return ledgers[_accountHolder][ledgerList[_index]].ledger;
        else
            return address(0x0);
    }

    // confirm if an account holder exists or not
    function confirmAccountHolder(address _accountHolder) external view returns(bool){
        require(msg.sender==holderAddress || msg.sender==accountAddress || msg.sender==ledgerAddress);
        for(uint256 i=0; i<accountHolderList.length; i++){
            if(accountHolderList[i]==_accountHolder)
                return true;
        }
        return false;
    }
    
    //account holders can be created by message senders who are KYC verified clients or their managers
    //_holderName is the name of the account holder
    //_accountCreator should be a KYC verified client for which the account holder is created in books
    function createHolder(bytes32 _holderName, address _accountCreator) external returns(address){
        //only registered client or their managers can create account holder
        require(clientRef.isRegistered(_accountCreator, msg.sender));

        bytes memory _payload = abi.encodeWithSignature("initialize(bytes32, address, address)", _holderName, _accountCreator, address(this));

        // connect account holder to account
        address _accountHolder = deployMinimal(holderAddress, _payload);
        emit HolderCreated(_accountHolder, _holderName, _accountCreator);

        accountholders[_accountCreator][_accountHolder] = accountholder(_holderName, _accountHolder);
        accountHolderList.push(_accountHolder);
        return _accountHolder;
    }

    //ledger account factory with ledgers created by account holders (msg.sender to this function)
    function createLedger(bytes32 _ledgerName, bytes32 _ledgerGroup, address _accountCreator) external returns(address){
        //only account holders with registered clients can request ledger creation
        require(accountholders[_accountCreator][msg.sender].accountHolder!=address(0x0));

        // invoker of this function is the owner of the ledger that gets created
        bytes memory _payload = abi.encodeWithSignature("initialize(bytes32,bytes32,address,address,address)", _ledgerName, _ledgerGroup, _accountCreator, address(this), msg.sender);

        // Deploy proxy
        address _ledgerAccount = deployMinimal(ledgerAddress, _payload);
        emit LedgerCreated(_ledgerAccount, _ledgerName, _ledgerGroup, msg.sender);

        ledgers[msg.sender][_ledgerAccount] = ledger(_ledgerName, _ledgerGroup, _ledgerAccount);
        ledgerList.push(_ledgerAccount);
        return _ledgerAccount;
    }

    //account factory with accounts created in ledgers, and created by account holders
    function createAccount(bytes32 _accountName, address _ledger, bytes32 _currency, address _accountCreator, address _accountHolder) external returns(address){
        //only account holders with registered clients can request ledger creation
        require(accountholders[_accountCreator][_accountHolder].accountHolder!=address(0x0));

        (address viaCash, bytes32 tokenType) = via.getAddressAndType(_currency);
        
        if(viaCash!=address(0x0) && tokenType=="ViaCash"){
            bytes memory _payload = abi.encodeWithSignature("initialize(bytes32, address, address, address, address)", _accountName, _ledger, address(this), _accountCreator, _accountHolder);

            // Create account
            address _account = deployMinimal(accountAddress, _payload);
            emit AccountCreated(_account, _accountName, _ledger);

            accounts[_ledger][_account] = account(_accountName, _account, _currency);
            accountList.push(_account);
            return _account;
        }
    }
    
}
