// (c) Kallol Borah, 2020
// Sets up account and manages custody

pragma solidity 0.5.7;

import "./interfaces/VerifiedKYC.sol";
import "./interfaces/VerifiedClient.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";

contract Client is VerifiedClient, Initializable{

    struct access{
        uint256 login;
        uint256 logout;  
        address manager;
        bool aml;      
    }

    struct role{
        address manager;
        bytes32 country;
        bytes32 role;
    }

    //mapping of a manager to its sub managers
    mapping(address=> role) private roles;

    //storing submanagers
    address[] submanagers;

    //mapping of a client to login info
    mapping(address=> access) private info;

    //mapping of a client to its custody account
    mapping(address => address) private custody;

    //mapping of a manager to its clients
    mapping(address => address[]) private clients;

    //kyc contract
    VerifiedKYC private Kyc;

    //aml manager
    address private amlManager;

    //owner is someone who can upgrade this contract
    function initialize(address _kyc) public {
        Kyc = VerifiedKYC(_kyc);
        amlManager = msg.sender;
    }

    //only the user or its manager can set the custody account address
    function setCustody(address _client, address _account) external {
        require(_client==msg.sender || info[_client].manager==msg.sender);
        if(Kyc.getStatus(_client)){
            custody[_client] = _account;
        }
    }

    //only the user or its manager can get the custody account address
    function getCustody(address _client) external view returns(address){
        require(_client==msg.sender || info[_client].manager==msg.sender);
        if(Kyc.getStatus(_client)){
            return custody[_client];
        }
    }

    //access info can only be set by user
    function setAccess(bool login) external{
        if(Kyc.getStatus(msg.sender)){
            if(login)
                info[msg.sender].login=now;
            else
                info[msg.sender].logout=now;
        }
    }

    //access data for a user can only be retrieved by itself or its manager
    function getAccess(address _client) external view returns(uint256, uint256){
        require(_client==msg.sender || info[_client].manager==msg.sender);
        if(Kyc.getStatus(_client)){
            return (info[_client].login, info[_client].logout);
        }
    }

    //when a user is not registered, the AML manager can set the user's KYC manager
    //when a user is registered or KYC passed, both the AML manager and KYC manager can reset the user's manager
    function setManager(address _client, address _manager) external{
        if(amlManager==msg.sender && _client!=_manager){
            if(!Kyc.getStatus(_client)){
                info[_client].manager = _manager;
                clients[_manager].push(_client);
            }
        }
        else if(info[_client].manager==msg.sender && _client!=_manager){
            if(Kyc.getStatus(_client)){
                info[_client].manager = _manager;
                clients[_manager].push(_client);
            }
        }
    }

    // manager identity can be retrieved by user or its manager only
    function getManager(address _client) external view returns(address){
        return info[_client].manager;
    }

    //checks if client is registered or not
    function isRegistered(address _client) external view returns(bool){
        if(info[_client].login!=0)
            return true;
        else
            return false;
    }

    //sets AML status
    function setAMLStatus(address _client, bool status) external {
        require(msg.sender==info[_client].manager || msg.sender==amlManager);  
        info[_client].aml = status;
    }

    //gets AML status
    function getAMLStatus(address _client) external view returns(bool){
        require(msg.sender==info[_client].manager || msg.sender==amlManager);  
        return info[_client].aml;
    }

    //get clients for specified manager with registration status set to true or false
    function getClients(address _manager, bool _status) external returns(address[] memory){
        require(msg.sender==_manager);
        return getClientsForManager(_manager, _status);
    }

    function getClientsForManager(address _manager, bool _status) private view returns(address[] memory){
        address[] memory _clients = clients[_manager];
        address[] memory resp;
        uint j = 0;
        for(uint i=0; i<_clients.length; i++){
            if(_status){
                if(info[_clients[i]].login!=0)
                    resp[j] = _clients[i];
                    j=j+1;
            }
            else{
                if(info[_clients[i]].login==0)
                    resp[j] = _clients[i];
                    j=j+1;
            }
        }
        return resp;
    }

    //add role
    function addRole(address _submanager, bytes32 _country, bytes32 _role) external{
        require(_role=="Custodian" || _role=="Issuer" || _role=="DP" || _role=="KYCAML");
        require(getClientsForManager(msg.sender, true).length>0);
        submanagers.push(_submanager);
        roles[_submanager].manager = msg.sender;
        roles[_submanager].country = _country;
        roles[_submanager].role = _role;
    }

    //get roles for manager
    function getRole(bytes32 _country) external view returns(bytes32){
        for(uint i=0; i<submanagers.length; i++){
            if(submanagers[i]==msg.sender && roles[submanagers[i]].country==_country){
                return roles[submanagers[i]].role;
            }
        }
        return "";
    }

    //remove role
    function removeRole(address _submanager, bytes32 _country, bytes32 _role) external{
        require(_role=="Custodian" || _role=="Issuer" || _role=="DP" || _role=="KYCAML");
        for(uint i=0; i<submanagers.length; i++){
            if( submanagers[i]==_submanager &&
                roles[submanagers[i]].role==_role && 
                roles[submanagers[i]].country==_country &&
                roles[submanagers[i]].manager==msg.sender){
                delete(roles[submanagers[i]]);
            }
        }
    }   

}
