// (c) Kallol Borah, 2020
// Implementation of a Verified account holder

pragma solidity 0.5.7;

import "../interfaces/VerifiedSystem.sol";
import "../interfaces/VerifiedLedger.sol";
import "../interfaces/VerifiedHolder.sol";
import "abdk-libraries-solidity/ABDKMathQuad.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";


contract Holder is Initializable, VerifiedHolder{

    using ABDKMathQuad for uint256;
    using ABDKMathQuad for int256;
    using ABDKMathQuad for bytes16;

    VerifiedSystem private factory;

    bytes32 holderName; 

    address accountCreator;

    address accountHolder;

    mapping(address => uint256) entries;   

    struct entry{
        bytes32 ledgerName;
        bytes32 ledgerGroup;
        bytes16 ledgerBalance;
    }

    mapping(address => mapping (uint256 => entry)) statement;

    address[] party;
    bytes16[] amount;
    uint256[] date;
    bytes32[] description;
    bytes32[] transaction;

    //initiliaze proxies
    function initialize(bytes32 _holderName, address _accountCreator, address _owner, address _accountHolder) public initializer{
        // this account holder can be referred from this account
        factory = VerifiedSystem(_owner);
        holderName = _holderName;
        accountCreator = _accountCreator;
        accountHolder = _accountHolder;
    }

    function updateAccountStatement() external{
        require(accountCreator==msg.sender);
        // get ledgers for this account holder
        address[] memory ledgers = factory.getAccountLedgers(address(this));
        for(uint256 i=0; i<ledgers.length; i++){
            bytes16 balance;
            bytes32 name;
            bytes32 group;
            (balance, name, group) = VerifiedLedger(ledgers[i]).getBalance(); 
            bool found = false;
            uint256 p = 0;   
            while(!found && statement[msg.sender][p].ledgerName!=""){
                if(statement[msg.sender][p].ledgerName==name && statement[msg.sender][p].ledgerGroup==group){
                    found = true;
                    statement[msg.sender][p].ledgerBalance = balance;
                }
                p=p+1;
            }        
            if(!found){
                statement[msg.sender][p].ledgerName = name;
                statement[msg.sender][p].ledgerGroup = group;
                statement[msg.sender][p].ledgerBalance = balance;
                p=p+1;
            }
            entries[msg.sender] = p;
        }
    }

    // returns number of trial balance statement entries updated for account holder
    function getEntries() external view returns(uint256){
        return entries[msg.sender];
    }

    function getAccountStatement(uint256 statementIndex) external view returns(bytes32, bytes32, bytes16){
        require(accountCreator==msg.sender);
        return(statement[msg.sender][statementIndex].ledgerName, statement[msg.sender][statementIndex].ledgerGroup, statement[msg.sender][statementIndex].ledgerBalance);
    }

    // gets all account entries for an account creator
    function getTransactions() external view returns(address[] memory, bytes16[] memory, bytes32[] memory, uint256[] memory, bytes32[] memory){
        // ensure the msg sender owns this ledger
        require(accountCreator==msg.sender);
        uint _entries = party.length;
        address[] memory _party = new address[](_entries);
        bytes32[] memory _txType = new bytes32[](_entries);
        bytes32[] memory _txDescription = new bytes32[](_entries);
        bytes16[] memory _txAmount = new bytes16[](_entries);
        uint256[] memory _date = new uint256[](_entries);
        for(uint256 k=0; k<_entries; k++){
            _party[k] = party[k];
            _txType[k] = transaction[k];
            _txDescription[k] = description[k];
            _date[k] = date[k];
            _txAmount[k] = amount[k];
        }
        return(_party, _txAmount, _txType, _date, _txDescription);
    }
    
    function fetchTransactions(uint256 _txDate) external {
        // ensure the msg sender owns this ledger
        require(accountCreator==msg.sender);
        delete party;
        delete amount;
        delete transaction;
        delete date;
        delete description;
        address[] memory ledgers = factory.getAccountLedgers(address(this));
        for(uint256 i=0; i<ledgers.length; i++){
            (address[] memory _party, bytes16[] memory _amount, bytes32[] memory _txType, uint256[] memory _date, bytes32[] memory _description) 
                = VerifiedLedger(ledgers[i]).getEntries(_txDate);
            for(uint256 j=0; j<_party.length; j++){
                party.push(_party[j]);
                amount.push(_amount[j]);
                transaction.push(_txType[j]);
                date.push(_date[j]);
                description.push(_description[j]);
            }
        }
        
    }

    function createLedger(bytes32 _ledgerName, bytes32 _ledgerGroup) external {
        require(accountCreator==msg.sender);
        factory.createLedger(_ledgerName, _ledgerGroup, accountCreator, accountHolder);
    }

}
