// (c) Kallol Borah, 2020
// Implementation of a general ledger account

pragma solidity 0.5.7;

import "../interfaces/VerifiedSystem.sol";
import "../interfaces/VerifiedAccount.sol";
import "../interfaces/VerifiedLedger.sol";
import "abdk-libraries-solidity/ABDKMathQuad.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";


contract Ledger is VerifiedLedger, Initializable{

    using ABDKMathQuad for uint256;
    using ABDKMathQuad for int256;
    using ABDKMathQuad for bytes16;

    VerifiedSystem private factory;

    bytes32 public ledgerName;

    bytes32 ledgerGroup;

    bytes16 ledgerBalance;

    address accountCreator;

    address accountHolder;

    //initiliaze proxies
    function initialize(bytes32 _ledgerName, bytes32 _ledgerGroup, address _accountCreator, address _owner, address _accountHolder) public initializer{
        // this ledger can be owned by only one account holder (client)
        factory = VerifiedSystem(_owner);
        ledgerName = _ledgerName;
        ledgerGroup = _ledgerGroup;
        ledgerBalance = 0;
        accountCreator = _accountCreator;
        accountHolder = _accountHolder;
    }

    // sum up balances of all accounts in this ledger account
    function getBalance() external returns (bytes16, bytes32, bytes32){
        // ensure the msg sender owns this ledger
        require(accountHolder==msg.sender);        
        address[] memory accounts = factory.getLedgerAccounts(address(this));
        for(uint256 i=0; i<accounts.length; i++){
            ledgerBalance = ABDKMathQuad.add(ledgerBalance, VerifiedAccount(accounts[i]).getBalance(accountHolder));
        }
        return(ledgerBalance, ledgerName, ledgerGroup);
    }

    //needs to be called by the owner of this ledger to create an account
    //_accountName is the name of the account proxy to create
    function createAccount(bytes32 _accountName, bytes32 _currency) external {
        require(accountCreator==msg.sender);
        factory.createAccount(_accountName, _currency, accountCreator, accountHolder);
    }

}
