// (c) Kallol Borah, 2020
// Implementation of the Verified accounts factory

pragma solidity 0.5.7;

import "../interfaces/VerifiedHolder.sol";
import "../interfaces/VerifiedLedger.sol";
import "../interfaces/VerifiedAccount.sol";
import "../interfaces/VerifiedClient.sol";
import "../interfaces/VerifiedSystem.sol";
import "../interfaces/ViaFactory.sol";
import "@openzeppelin/upgrades/contracts/upgradeability/ProxyFactory.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";

contract System is VerifiedSystem, ProxyFactory, Initializable {

    // first, a client creates an account holder representing an account statement
    struct accountholder{
        bytes32 holderName;
        address accountHolder;
    }

    // mapping of account creator to account holder
    mapping(address => address[]) private accountHolderList;

    // mapping of account holder address to its info
    mapping(address => accountholder) public accountholders;

    // then, a ledger is created in an account statement (ie, for an account holder)
    struct ledger{
        bytes32 ledgerName;
        bytes32 group;
        address ledger;
    }

    // mapping of account holder to ledger
    mapping(address => address[]) private ledgerList;

    // mapping of ledger address to its info
    mapping(address => ledger) public ledgers;

    // then, an account is created in a ledger
    struct account{
        bytes32 accountName;
        address account;
        bytes32 currency;
    }

    // mapping of ledger to account
    mapping(address => address[]) private accountList;

    // mapping of an account address to its info
    mapping(address => account) public accounts;

    //notifying listeners
    event LedgerCreated(address indexed ledger, bytes32 ledgerName, bytes32 group, address accountHolder);
    event AccountCreated(address indexed account, bytes32 accountName, address ledger);
    event HolderCreated(address indexed holder, bytes32 holderName, address accountCreator);

    //references to contract
    VerifiedClient private clientRef;
    VerifiedAccount private accountRef;
    VerifiedHolder private holderRef;
    VerifiedLedger private ledgerRef;
    ViaFactory private via;

    //addresses fo contracts
    address accountAddress;
    address holderAddress;
    address ledgerAddress;

    //owner is someone who can upgrade this contract
    function initialize(address _ledger, address _account, address _holder, address _client, address _viaFactory) public initializer{
        clientRef = VerifiedClient(_client);
        accountRef = VerifiedAccount(_account);
        holderRef = VerifiedHolder(_holder);
        ledgerRef = VerifiedLedger(_ledger);
        accountAddress = _account;
        holderAddress = _holder;
        ledgerAddress = _ledger;
        via = ViaFactory(_viaFactory);
    }

    // return last account holder created by account creator
    function getAccountHolder(address accountCreator) external view returns(address){
        //return accountholders[accountCreator].accountHolder;
        address[] memory accountHolders = accountHolderList[accountCreator];
        return accountHolders[accountHolders.length-1];
    }

    // return last account ledger created by account holder
    function getAccountLedger(address accountHolder) external view returns(address){
        //return ledgers[accountCreator][accountHolder].ledger;
        address[] memory accountLedgers = ledgerList[accountHolder];
        return accountLedgers[accountLedgers.length-1];
    }

    // return last account created by account ledger
    function getLedgerAccount(address accountLedger) external view returns(address){
        //return accounts[accountHolder][accountLedger].account;
        address[] memory ledgerAccounts = accountList[accountLedger];
        return ledgerAccounts[ledgerAccounts.length-1];
    }

    // return account holder addresses for account creator
    function getAccountHolders(address accountCreator) external view returns(address[] memory){
        return accountHolderList[accountCreator];
    }

    // return account ledger addresses for account holder
    function getAccountLedgers(address accountHolder) external view returns(address[] memory){
        return ledgerList[accountHolder];
    }
    
    // return account addresses for account ledger
    function getLedgerAccounts(address accountLedger) external view returns(address[] memory){
        return accountList[accountLedger];
    }

    // return details of account holder
    function getHolderDetails(address holder) external view returns(bytes32){
        return accountholders[holder].holderName;
    }

    // return details of ledger
    function getLedgerDetails(address _ledger) external view returns(bytes32, bytes32){
        return (ledgers[_ledger].ledgerName, ledgers[_ledger].group);
    }

    // return details of account
    function getAccountDetails(address _account) external view returns(bytes32, bytes32){
        return (accounts[_account].accountName, accounts[_account].currency);
    }

    // confirm if an account holder exists or not
    function confirmAccountHolder(address _accountHolder) external view returns(bool){
        if(accountholders[_accountHolder].accountHolder!=address(0x0))
            return true;
        else
            return false;
    }
    
    //account holders can be created by message senders who are KYC verified clients
    //_holderName is the name of the account holder
    //_accountHolder is the client's counter party for which the account is created in books
    function createHolder(bytes32 _holderName, address _accountHolder) external {
        //only registered client can create account holder (client's counter party in client's books of account)
        //require(clientRef.isRegistered(msg.sender));

        bytes memory _payload = abi.encodeWithSignature("initialize(bytes32,address,address,address)", _holderName, msg.sender, _accountHolder, address(this));

        //create account holder
        address _holder = deployMinimal(holderAddress, _payload);
        emit HolderCreated(_holder, _holderName, _accountHolder);
         
        //map client to account holder
        accountholders[_holder] = accountholder(_holderName, _holder);
        accountHolderList[_accountHolder].push(_holder);
    }

    //ledger account factory with ledgers created by account holders (msg.sender to this function)
    function createLedger(bytes32 _ledgerName, bytes32 _ledgerGroup, address _accountCreator, address _accountHolder) external {
        //only account holders can request ledger creation
        require(accountholders[msg.sender].accountHolder==msg.sender);

        // invoker of this function is the owner of the ledger that gets created
        bytes memory _payload = abi.encodeWithSignature("initialize(bytes32,bytes32,address,address,address)", _ledgerName, _ledgerGroup, _accountCreator, address(this), msg.sender);

        //create ledger
        address _ledger = deployMinimal(ledgerAddress, _payload);
        emit LedgerCreated(_ledger, _ledgerName, _ledgerGroup, msg.sender);

        //map account holder to ledger
        ledgers[_ledger] = ledger(_ledgerName, _ledgerGroup, _ledger);
        ledgerList[msg.sender].push(_ledger);
    }

    //account factory with accounts created in ledgers, and created by account holders
    function createAccount(bytes32 _accountName, bytes32 _currency, address _accountCreator, address _accountHolder) external {
        //only ledger accounts with registered account holders can request account creation
        require(ledgers[msg.sender].ledger==msg.sender);

        address viaCash = via.getTokenByNameType(_currency, "ViaCash");

        if(viaCash!=address(0x0)){
            bytes memory _payload = abi.encodeWithSignature("initialize(bytes32,address,address,address,address)", _accountName, msg.sender, address(this), _accountCreator, _accountHolder);

            // Create account
            address _account = deployMinimal(accountAddress, _payload);
            emit AccountCreated(_account, _accountName, msg.sender);

            //map accountholder + ledger to account
            accounts[_account] = account(_accountName, _account, _currency);
            accountList[msg.sender].push(_account);
        }
        
    }
    
}
