// SPDX-License-Identifier: MIT

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

interface VerifiedHolder{

    struct accountEntry{        
        bytes32 txType;
        uint256 txDate;
        bytes32 txDescription;
        bytes32 voucherType;
        uint256 txAmount;
        address party;
    }

    function updateAccountStatement() external;

    function getAccountStatement(uint256 statementIndex) external view returns(bytes32, bytes32, bytes16);

    function createLedger(bytes32 _ledgerName, bytes32 _ledgerGroup) external;

    function getEntries() external view returns(uint256);

    function fetchTransactions(uint256 _startDate, uint256 _endDate) external;

    function getTransactions(uint256 _toDate, bytes32 _currency) external view returns(uint256);

    function getEntry(uint256 _startIndex, uint256 _endIndex, uint256 _toDate, bytes32 _currency) external view returns(accountEntry[] memory);

    function setBlock(uint256 _block) external;

    function getBlock() external view returns(uint256);
}
