pragma solidity ^0.7.1;

import "./interfaces/VerifiedProducts.sol";

contract Products is VerifiedProducts {

    //mapping product issued to product reference
    mapping(address => bytes32) private issues;

    modifier onlyBridge(address sender){
        
        _;
    }

    function issueProduct(address _issue, bytes32 _ref) onlyBridge(msg.sender) override external{
        issues[_issue] = _ref;
    } 

    function checkProduct(address _issue) onlyBridge(msg.sender) override external view returns(bool){
        if(issues[_issue]!='')
            return true;
        else
            return false;
    }

}
