pragma solidity ^0.7.1;

import "./interfaces/VerifiedProducts.sol";
import '@balancer-labs/v2-solidity-utils/contracts/openzeppelin/Ownable.sol';

contract Products is VerifiedProducts, Ownable {

    //mapping product issued to product reference
    mapping(address => bytes32) private issues;

    //bridge
    address bridge;

    function initialize(address _signer) onlyOwner public {
        bridge = _signer;
    }

    function setSigner(address _signer) onlyOwner override external{
        bridge = _signer;
    } 

    function issueProduct(address _issue, bytes32 _ref, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                            override external {
        bytes32 payloadHash = keccak256(abi.encode(_hashedMessage, "L2toL1"));
        bytes32 messageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", payloadHash));
        require(ecrecover(messageHash, _v, _r, _s)== bridge);
        issues[_issue] = _ref;
    } 

    function checkProduct(address _issue) 
                            override external view returns(bool){
        if(issues[_issue]!='')
            return true;
        else
            return false;
    }

    function getProductReference(address _issue) override external view returns(bytes32){
        return issues[_issue];
    }

}
