// Interface for creating secondary trading pools and settling secondary trades
//"SPDX-License-Identifier: BUSL1.1"

pragma solidity 0.7.1;
pragma experimental ABIEncoderV2;

import "./ITrade.sol";
import "./IOrder.sol";

interface ISettlor {

    //enum SettlementStatus { Confirm, Reject, Pending, Fail }

    /*enum FeeType {  Regulatory, 
                    Tax,
                    Commission,
                    ExchangeFees,
                    Stamp,
                    Levy,
                    Other,
                    Markup,
                    ConsumptionTax,
                    Transaction, 
                    Conversion,
                    Agent,
                    TransferFee,
                    SecurityLending,
                    TradeReporting, 
                    TaxPrincipalAmount,
                    TaxAccruedInterest,
                    NewIssuanceFee,
                    ServiceFee,
                    VAT,
                    GST
                    }*/
    enum JoinKind {
        INIT,
        EXACT_TOKENS_IN_FOR_BPT_OUT,
        TOKEN_IN_FOR_EXACT_BPT_OUT,
        ALL_TOKENS_IN_FOR_EXACT_BPT_OUT
    }

    struct subscriptions{
        address investor;
        address asset;
        address currency;
        uint256 amount;   
        uint256 price;     
    }

    struct settlement{  address transferor;
                        address transferee;
                        address security;
                        bytes32 status;
                        bytes32 transferorDPID;
                        bytes32 transfereeDPID;
                        address currency;
                        uint256 price;
                        uint256 askprice;
                        uint256 unitsToTransfer;
                        uint256 consideration;
                        uint256 executionDate;
                        IOrder orderPool;
                        bytes32 partyRef;
                        bytes32 counterpartyRef;
                    }

    function issueSecondary(address security, address currency, uint256 securityAmount, uint256 minSecurityOrder, uint256 cashAmount) external;

    function requestSettlement(ITrade.trade memory tradeToReport, IOrder orderbook) external;

    function getSettlementRequests(bytes32 dpid, bytes32 poolId) external view returns(bytes32[] memory);

    function getSettlementRequest(bytes32 ref) external view returns(settlement memory);

    function setSettlementStatus(bytes32 ref, bytes32 status) external;

    function getTrade(bytes32 ref) external view returns(uint256 b, uint256 a);

    function getTradingFeeCollected(address _token) external returns(uint256);

    function getSubscribers(bytes32 poolId) external view returns(subscriptions[] memory);

}
