pragma solidity 0.7.1;
pragma experimental ABIEncoderV2;

import "./interfaces/ISettlor.sol";
import "./interfaces/IOrder.sol";
import "./interfaces/ITrade.sol";
import './interfaces/IFactory.sol';
import './interfaces/VerifiedClient.sol';
import "./interfaces/ISecondaryIssuePool.sol";
import "./interfaces/ISecondaryIssuePoolFactory.sol";

import "@balancer-labs/v2-solidity-utils/contracts/math/Math.sol";
import "@balancer-labs/v2-solidity-utils/contracts/openzeppelin/ERC20.sol";
import "@balancer-labs/v2-solidity-utils/contracts/openzeppelin/IERC20.sol";
import "@balancer-labs/v2-solidity-utils/contracts/openzeppelin/Ownable.sol";

contract SecondaryIssueManager is ISettlor, Ownable {

    using Math for uint256;

    // balancer variables
    ISecondaryIssuePoolFactory public factory;

    // reference to the Verified factory contract that this asset manager checks security token issues with
    IFactory products;

    IVault vault;

    //fee
    uint256 fee;

    // mapping settlement ref to trade execution data
    mapping(bytes32 => ISettlor.settlement) settlements;

    //mapping depository participants to trades they are part of
    mapping(bytes32 => bytes32[]) private trades;

    //mapping pool address to pool id
    mapping(address => bytes32) private pools;

    //mapping pool id to security token traded
    mapping(bytes32 => address) private poolSecurity;

    //mapping pool id to investors
    mapping(bytes32 => subscriptions[]) internal investors; 

    // reference to the Verified Client contract
    VerifiedClient client;

    address private bridge;

    address private distribution;

    event tradeSettled(address indexed transferor,
                        address indexed transferee,
                        uint256 unitsToTransfer,
                        address security,
                        uint256 price,
                        address currency
                    );

    event subscribers(address security, address platform, bytes32 poolId, subscriptions[] investors);

    /**
        Initializes this asset management contract
        @param  _factory            reference to the secondary issue pool factory contract
        @param  _issueFeePercentage  percentage of trading fee to be charged by the asset manager
        @param  _client             address of Verified client contract
        @param  _vault              address of balancer vault
     */
    function initialize(address _factory, uint256 _issueFeePercentage, address _client, address _vault, address _distribution, address _products) onlyOwner public {
        factory = ISecondaryIssuePoolFactory(_factory);
        fee = _issueFeePercentage;
        client = VerifiedClient(_client);
        vault = IVault(_vault);
        products = IFactory(_products);
        distribution = _distribution;
    }

    function setSigner(address _signer) onlyOwner external{
        bridge = _signer;
    }

    /**
        Joins existing (secondary) tokenized assets into pool by primary issue token holders
        @param  security            reference to asset issued
        @param  currency            reference to cash (settlement) token
        @param  securityAmount      amount of security issued in pool
        @param  cashAmount          amount of currency issued in pool
     */
    function issueSecondary(address security, address currency, uint256 securityAmount, uint256 cashAmount, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) override external {
        bytes32 payloadHash = keccak256(abi.encode(_hashedMessage, "L2toL1"));
        bytes32 messageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", payloadHash));
        require(ecrecover(messageHash, _v, _r, _s)== bridge || (IERC20(security).balanceOf(msg.sender)>=securityAmount 
                            && IERC20(currency).balanceOf(msg.sender)>=cashAmount), "Unauthorized access to secondary issue manager");
        address pool = factory.create(
            ERC20(security).name(),
            ERC20(security).symbol(),
            security,
            currency,
            securityAmount,
            fee
        );                
        bytes32 poolId = ISecondaryIssuePool(pool).getPoolId();  
        poolSecurity[poolId] = security; 
        pools[pool] = poolId;
        // join the pool here                
        /*IAsset[] memory assetPair = new IAsset[](3);
        assetPair[0] = IAsset(security);
        assetPair[1] = IAsset(pool);
        assetPair[2] = IAsset(currency);
        uint256[] memory amountsIn = new uint256[](3);
        amountsIn[0] = securityAmount;
        amountsIn[1] = 0;
        amountsIn[2] = cashAmount;*/
        (IERC20[] memory tokens, , ) = vault.getPoolTokens(poolId);
        IAsset[] memory assets = _convertERC20sToAssets(tokens);
        uint256[] memory maxAmountsIn = new uint256[](tokens.length);
        for (uint256 t; t < tokens.length; t++) {
            if(tokens[t]==IERC20(security))
                maxAmountsIn[t] = securityAmount;
            else if(tokens[t]==IERC20(currency))
                maxAmountsIn[t] = cashAmount;
        }
        IVault.JoinPoolRequest memory request = IVault.JoinPoolRequest({
            assets: assets,
            maxAmountsIn: maxAmountsIn,
            userData: '0x',
            fromInternalBalance: false
        });         
        vault.joinPool(poolId, address(this), address(this), request);
    }

    /**
        Used by Secondary pools to request settlement for trades
        @param  tradeToReport     data structure holding order matching data
     */
    function requestSettlement(ITrade.trade memory tradeToReport, IOrder orderbook) override external {
        require(pools[msg.sender]!="", "Settlement requested from invalid pool");
        IOrder.order memory orderByParty = IOrder(orderbook).getOrder(tradeToReport.partyRef);
        IOrder.order memory orderByCounterparty = IOrder(orderbook).getOrder(tradeToReport.counterpartyRef);
        address securityTraded = IOrder(orderbook).getSecurity(); 
        address currencySettled = IOrder(orderbook).getCurrency();
        address party = orderByParty.tokenIn==securityTraded ? orderByParty.party : orderByCounterparty.party;
        address counterparty = orderByCounterparty.tokenIn==securityTraded ? orderByCounterparty.party : orderByParty.party;
        uint256 amount = orderByParty.tokenIn==securityTraded ? tradeToReport.partyInAmount : tradeToReport.counterpartyInAmount;
        bytes32 _transfereeDPID = getTransferAgent(party);
        bytes32 _transferorDPID = getTransferAgent(counterparty);
        ISettlor.settlement memory tradeToSettle = ISettlor.settlement({
            transferor: party,
            transferee: counterparty,
            security: securityTraded,
            status: "Pending",
            currency: currencySettled,
            price: tradeToReport.price,
            askprice: Math.div(orderByParty.tokenIn==currencySettled ? tradeToReport.partyInAmount : tradeToReport.counterpartyInAmount, amount, false),
            unitsToTransfer: amount,
            consideration: Math.mul(tradeToReport.price, amount),
            executionDate: tradeToReport.dt,
            partyRef: orderByParty.tokenIn==securityTraded ? tradeToReport.partyRef : tradeToReport.counterpartyRef,
            counterpartyRef: orderByCounterparty.tokenIn==securityTraded ? tradeToReport.counterpartyRef : tradeToReport.partyRef,
            transferorDPID: _transferorDPID,
            transfereeDPID: _transfereeDPID,
            orderPool: orderbook
        });
        bytes32 ref = keccak256(abi.encodePacked(tradeToReport.partyRef, tradeToReport.counterpartyRef));
        if(settlements[ref].transferor==address(0x0)){
            settlements[ref] = tradeToSettle;
            investors[IOrder(orderbook).getPoolId()].push(ISettlor.subscriptions(counterparty, securityTraded, currencySettled, amount, tradeToReport.price));
            trades[tradeToSettle.transferorDPID].push(ref);
            trades[tradeToSettle.transfereeDPID].push(ref);
        }
    }

    /**
        Fetches trade settlement requests for depository participant
        @param  dpid         identifier of depository participant (eg, transfer agent) that is authorized to settle the asset 
     */
    function getSettlementRequests(bytes32 dpid) override external view returns(bytes32[] memory){
        (bytes32 role, ) = client.getRole(msg.sender);
        require(role=='DP', "Settlement requests can only be fetched by a depositary participant");
        return trades[dpid];
    }

    /**
        Fetches a specific trade settlement request        
        @param  ref         reference to the trade
     */
    function getSettlementRequest(bytes32 ref) override external view returns(settlement memory){
        require(ref!="", "Settlement requests can only be fetched by a depositary participant"); 
        return settlements[ref];
    }

    /**
        Sets settlement status for trades executed
        @param  ref         reference to the trade executed
        @param  status      settlement status
     */
    function setSettlementStatus(bytes32 ref, bytes32 status) override external{
        (bytes32 role, bytes32 id) = client.getRole(msg.sender);
        require(role=='DP', "Settlement not done by depositary participant");
        bytes32 transferorDPID = settlements[ref].transferorDPID;
        bytes32 transfereeDPID = settlements[ref].transfereeDPID;
        require(id==transferorDPID || id==transfereeDPID, "Settlement done by invalid depositary participant");
        require(status=="Confirm" || status=="Reject", "Settlement status invalid");
        settlements[ref].status = status;
        bool success;
        if(status=="Confirm"){
            uint256 pay = Math.mul(settlements[ref].price, settlements[ref].unitsToTransfer);
            if(IERC20(settlements[ref].currency).transferFrom(settlements[ref].transferee, settlements[ref].transferor, pay)){                
                success = IERC20(settlements[ref].security).transferFrom(settlements[ref].transferor, 
                                                                settlements[ref].transferee, 
                                                                settlements[ref].unitsToTransfer);
                if(success){
                    IOrder(settlements[ref].orderPool).orderFilled(settlements[ref].partyRef, settlements[ref].counterpartyRef, settlements[ref].executionDate);
                    emit tradeSettled(settlements[ref].transferor, 
                                        settlements[ref].transferee, 
                                        settlements[ref].unitsToTransfer,
                                        settlements[ref].security,
                                        settlements[ref].price,
                                        settlements[ref].currency
                                    );
                    for(uint i=0; i<trades[transferorDPID].length; i++){
                        if(trades[transferorDPID][i]==ref){
                            if(i==trades[transferorDPID].length-1)
                                delete trades[transferorDPID][i];
                            else
                                trades[transferorDPID][i] = trades[transferorDPID][i+1];
                        }
                    }
                    for(uint i=0; i<trades[transfereeDPID].length; i++){
                        if(trades[transfereeDPID][i]==ref){
                            if(i==trades[transfereeDPID].length-1)
                                delete trades[transfereeDPID][i];
                            else
                                trades[transfereeDPID][i] = trades[transfereeDPID][i+1];
                        }
                    }
                    settlements[ref].status = "Confirm";
                }
                else{
                    IOrder(settlements[ref].orderPool).revertTrade(settlements[ref].partyRef, settlements[ref].unitsToTransfer, IOrder.Order.Sell, settlements[ref].executionDate);
                    IOrder(settlements[ref].orderPool).revertTrade(settlements[ref].counterpartyRef, settlements[ref].consideration, IOrder.Order.Buy, settlements[ref].executionDate);
                    IERC20(settlements[ref].currency).transferFrom(settlements[ref].transferor, settlements[ref].transferee, pay);
                    settlements[ref].status = "Fail";
                }
            }
        }
        else if(status=="Reject"){
            IOrder(settlements[ref].orderPool).revertTrade(settlements[ref].partyRef, settlements[ref].unitsToTransfer, IOrder.Order.Sell, settlements[ref].executionDate);
            IOrder(settlements[ref].orderPool).revertTrade(settlements[ref].counterpartyRef, settlements[ref].consideration, IOrder.Order.Buy, settlements[ref].executionDate);
            settlements[ref].status = "Reject";
        }            
    }

    /**
        Retrieve investors in secondary pools
        @param  poolId  pool identity
     */
    function getSubscribers(bytes32 poolId, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) override external returns(subscriptions[] memory){
        bytes32 payloadHash = keccak256(abi.encode(_hashedMessage, "L2toL1"));
        bytes32 messageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", payloadHash));
        require(ecrecover(messageHash, _v, _r, _s)== bridge || products.getHolder(products.getSecurity(poolSecurity[poolId]))==msg.sender, "Unauthorized access viewing subscribers in secondary pool.");
        emit subscribers(poolSecurity[poolId], address(this), poolId, investors[poolId]);
        return investors[poolId];
    }

    /**
        Retrieve trade settlement
        @param  ref settlement reference
     */
    function getTrade(bytes32 ref) external view override returns (uint256, uint256) {
        return (settlements[ref].price, settlements[ref].askprice);
    }

    /**
        Gets transfer agent for any of the parties in the trade
        @param  party         reference to the party or counterparty in a trade
     */
    function getTransferAgent(address party) private view returns(bytes32){
        return(client.getTransferAgent(party));
    } 

    /**
        Fetch trading fee collected in Balancer vault
        @param  _token  address of token in which fee is collected
     */
    function getTradingFeeCollected(address _token) override external returns(uint256){
        require(msg.sender==distribution, "Unauthorized fee collection request");
        IERC20[] memory tokens = new IERC20[](1);
        tokens[0] = IERC20(_token);
        uint256[] memory amounts = vault.getProtocolFeesCollector().getCollectedFeeAmounts(tokens);
        vault.getProtocolFeesCollector().withdrawCollectedFees(tokens, amounts, distribution);
        return amounts[0];
    }   

    /**
     * @dev This helper function is a fast and cheap way to convert between IERC20[] and IAsset[] types
     */
    function _convertERC20sToAssets(IERC20[] memory tokens) internal pure returns (IAsset[] memory assets) {
        // solhint-disable-next-line no-inline-assembly
        assembly {
            assets := tokens
        }
    }

}

