pragma solidity 0.7.1;
pragma experimental ABIEncoderV2;

import "./interfaces/ISettlor.sol";
import "./interfaces/IOrder.sol";
import "./interfaces/ITrade.sol";
import './interfaces/VerifiedClient.sol';
import "./interfaces/ISecondaryIssuePool.sol";
import "./interfaces/ISecondaryIssuePoolFactory.sol";

import "@balancer-labs/v2-solidity-utils/contracts/math/Math.sol";
import "@balancer-labs/v2-solidity-utils/contracts/openzeppelin/ERC20.sol";
import "@balancer-labs/v2-solidity-utils/contracts/openzeppelin/IERC20.sol";
import "@balancer-labs/v2-solidity-utils/contracts/openzeppelin/Ownable.sol";

contract SecondaryIssueManager is ISettlor, Ownable {

    using Math for uint256;

    // balancer variables
    ISecondaryIssuePoolFactory public factory;

    //fee
    uint256 fee;

    // mapping settlement ref to trade execution data
    mapping(bytes32 => ISettlor.settlement) settlements;

    //mapping depository participants to trades they are part of
    mapping(bytes32 => bytes32[]) private trades;

    //mapping pool address to pool id
    mapping(address => bytes32) private pools;

    // reference to the Verified Client contract
    VerifiedClient client;

    address private bridge;

    event tradeSettled(address indexed transferor,
                        address indexed transferee,
                        uint256 unitsToTransfer,
                        address security,
                        uint256 price,
                        address currency
                    );

    /**
        Initializes this asset management contract
        @param  _factory            reference to the secondary issue pool factory contract
        @param  _issueFeePercentage  percentage of trading fee to be charged by the asset manager
        @param  _client             address of Verified client contract
     */
    function initialize(address _factory, uint256 _issueFeePercentage, address _client) onlyOwner public {
        factory = ISecondaryIssuePoolFactory(_factory);
        fee = _issueFeePercentage;
        client = VerifiedClient(_client);
    }

    function setSigner(address _signer) onlyOwner external{
        bridge = _signer;
    }

    /**
        Joins existing (secondary) tokenized assets into pool by primary issue token holders
        @param  security    reference to asset issued
        @param  currency    reference to cash (settlement) token
        @param  amount      amount of asset issued in pool
        @param  id          identifier (eg, ISIN) for asset issued
     */
    function issueSecondary(address security, address currency, uint256 amount, bytes32 id, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) override external {
        bytes32 payloadHash = keccak256(abi.encode(_hashedMessage, "L2toL1"));
        bytes32 messageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", payloadHash));
        require(ecrecover(messageHash, _v, _r, _s)== bridge || (IERC20(security).balanceOf(msg.sender)<=amount &&
                            IERC20(security).balanceOf(msg.sender)>0 && IERC20(currency).balanceOf(msg.sender)>0));
        address pool = factory.create(
            ERC20(security).name(),
            ERC20(security).symbol(),
            security,
            currency,
            amount,
            fee
        );
        pools[pool] = id;
        // initialize the pool here                
        //ISecondaryIssuePool(pool).initialize();
    }

    /**
        Used by Secondary pools to request settlement for trades
        @param  tradeToReport     data structure holding order matching data
     */
    function requestSettlement(ITrade.trade memory tradeToReport, IOrder orderbook) override external {
        require(pools[msg.sender]!="", "Settlement requested from invalid pool");
        address party = keccak256(abi.encodePacked(tradeToReport.partyTokenIn))==keccak256(abi.encodePacked("security")) ? tradeToReport.party : tradeToReport.counterparty;
        address counterparty = keccak256(abi.encodePacked(tradeToReport.partyTokenIn))==keccak256(abi.encodePacked("currency")) ? tradeToReport.party : tradeToReport.counterparty;
        uint256 amount = keccak256(abi.encodePacked(tradeToReport.partyTokenIn))==keccak256(abi.encodePacked("security")) ? tradeToReport.partyInAmount : tradeToReport.counterpartyInAmount;
        bytes32 _transfereeDPID = getTransferAgent(party);
        bytes32 _transferorDPID = getTransferAgent(counterparty);
        ISettlor.settlement memory tradeToSettle = ISettlor.settlement({
            transferor: party,
            transferee: counterparty,
            security: tradeToReport.security,
            status: "Pending",
            currency: tradeToReport.currency,
            price: tradeToReport.price,
            askprice: Math.div(keccak256(abi.encodePacked(tradeToReport.partyTokenIn))==keccak256(abi.encodePacked("currency")) ? tradeToReport.partyInAmount : tradeToReport.counterpartyInAmount, amount, false),
            unitsToTransfer: amount,
            consideration: Math.mul(tradeToReport.price, amount),
            executionDate: tradeToReport.dt,
            partyRef: keccak256(abi.encodePacked(tradeToReport.partyTokenIn))==keccak256(abi.encodePacked("security")) ? tradeToReport.partyRef : tradeToReport.counterpartyRef,
            counterpartyRef: keccak256(abi.encodePacked(tradeToReport.partyTokenIn))==keccak256(abi.encodePacked("security")) ? tradeToReport.partyRef : tradeToReport.counterpartyRef,
            transferorDPID: _transferorDPID,
            transfereeDPID: _transfereeDPID,
            orderPool: orderbook
        });
        bytes32 ref = keccak256(abi.encodePacked(tradeToReport.partyRef, tradeToReport.counterpartyRef));
        settlements[ref] = tradeToSettle;
        trades[tradeToSettle.transferorDPID].push(ref);
        trades[tradeToSettle.transfereeDPID].push(ref);
    }

    /**
        Fetches trade settlement requests for depository participant
        @param  dpid         identifier of depository participant (eg, transfer agent) that is authorized to settle the asset 
     */
    function getSettlementRequests(bytes32 dpid) override external view returns(bytes32[] memory){
        (bytes32 role, ) = client.getRole(msg.sender);
        require(role=='DP');
        return trades[dpid];
    }

    /**
        Fetches a specific trade settlement request        
        @param  ref         reference to the trade
     */
    function getSettlementRequest(bytes32 ref) override external view returns(settlement memory){
        require(ref!=""); 
        return settlements[ref];
    }

    /**
        Sets settlement status for trades executed
        @param  ref         reference to the trade executed
        @param  status      settlement status
     */
    function setSettlementStatus(bytes32 ref, bytes32 status) override external{
        (bytes32 role, bytes32 id) = client.getRole(msg.sender);
        require(role=='DP', "Settlement not done by depositary participant");
        bytes32 transferorDPID = settlements[ref].transferorDPID;
        bytes32 transfereeDPID = settlements[ref].transfereeDPID;
        require(id==transferorDPID || id==transfereeDPID, "Settlement done by invalid depositary participant");
        require(status=="Confirm" || status=="Reject", "Settlement status invalid");
        settlements[ref].status = status;
        bool success;
        if(status=="Confirm"){
            uint256 pay = Math.mul(settlements[ref].price, settlements[ref].unitsToTransfer);
            if(IERC20(settlements[ref].currency).transferFrom(settlements[ref].transferee, settlements[ref].transferor, pay)){                
                success = IERC20(settlements[ref].security).transferFrom(settlements[ref].transferor, 
                                                                settlements[ref].transferee, 
                                                                settlements[ref].unitsToTransfer);
                if(success){
                    IOrder(settlements[ref].orderPool).orderFilled(settlements[ref].partyRef, settlements[ref].counterpartyRef);
                    IOrder(settlements[ref].orderPool).tradeSettled(settlements[ref].partyRef, settlements[ref].counterpartyRef);
                    emit tradeSettled(settlements[ref].transferor, 
                                        settlements[ref].transferee, 
                                        settlements[ref].unitsToTransfer,
                                        settlements[ref].security,
                                        settlements[ref].price,
                                        settlements[ref].currency
                                    );
                    for(uint i=0; i<trades[transferorDPID].length; i++){
                        if(trades[transferorDPID][i]==ref){
                            if(i==trades[transferorDPID].length-1)
                                delete trades[transferorDPID][i];
                            else
                                trades[transferorDPID][i] = trades[transferorDPID][i+1];
                        }
                    }
                    for(uint i=0; i<trades[transfereeDPID].length; i++){
                        if(trades[transfereeDPID][i]==ref){
                            if(i==trades[transfereeDPID].length-1)
                                delete trades[transfereeDPID][i];
                            else
                                trades[transfereeDPID][i] = trades[transfereeDPID][i+1];
                        }
                    }
                    settlements[ref].status = "Confirm";
                }
                else{
                    IOrder(settlements[ref].orderPool).revertTrade(settlements[ref].partyRef, settlements[ref].unitsToTransfer, IOrder.Order.Sell);
                    IOrder(settlements[ref].orderPool).revertTrade(settlements[ref].counterpartyRef, settlements[ref].consideration, IOrder.Order.Buy);
                    IERC20(settlements[ref].currency).transferFrom(settlements[ref].transferor, settlements[ref].transferee, pay);
                    settlements[ref].status = "Fail";
                }
            }
        }
        else if(status=="Reject"){
            IOrder(settlements[ref].orderPool).revertTrade(settlements[ref].partyRef, settlements[ref].unitsToTransfer, IOrder.Order.Sell);
            IOrder(settlements[ref].orderPool).revertTrade(settlements[ref].counterpartyRef, settlements[ref].consideration, IOrder.Order.Buy);
            settlements[ref].status = "Reject";
        }            
    }

    function getTrade(bytes32 ref) external view override returns (uint256, uint256) {
        return (settlements[ref].price, settlements[ref].askprice);
    }

    /**
        Gets transfer agent for any of the parties in the trade
        @param  party         reference to the party or counterparty in a trade
     */
    function getTransferAgent(address party) private view returns(bytes32){
        return(client.getTransferAgent(party));
    }    

}

