// Verified bond manager
// (c) Kallol Borah, 2021

pragma solidity 0.5.7;

import "./interfaces/ViaCash.sol";
import "./interfaces/ViaBond.sol";
import "./interfaces/ViaFactory.sol";
import "./interfaces/VerifiedRates.sol";
import "./utilities/Pausable.sol";
import "./utilities/StringUtils.sol";
import "abdk-libraries-solidity/ABDKMathQuad.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/ownership/Ownable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/IERC20.sol";

contract Bond is ViaBond, Initializable, Ownable, Pausable {

    using stringutils for *;

    using ABDKMathQuad for uint256;
    using ABDKMathQuad for int256;
    using ABDKMathQuad for bytes16;

    //address of bridge
    address bridge;

    //name of Verified token (eg, Via-USD)
    string public name;
    string public symbol;
    bytes32 private bondName;
    bytes32 private bondCurrency;

    //verified token factory address
    ViaFactory private factory;
    
    //verified rates
    VerifiedRates private rates;   
    
    //a Verified bond has some value, corresponds to a fiat currency
    //can have many purchasers and a issuer that have agreed to a zero coupon rate which determines the start price of the bond
    //and a tenure in unix timestamps of seconds counted from 1970-01-01. Verified bonds are of one year tenure.
    struct bond{
        address[] counterParties;
        bytes16 parValue;               //face value of bond issued or purchased in the bond's currency
        bytes16 purchasedIssueAmount;   //amount purchased at price of bond for paidInCurrency
        bytes16 paidInAmount;           //amount paid in as collateral for issue of bond or as payment for purchase of bond
        bytes32 paidInCurrency;         //currency denomination of collateral for issue or payment for purchase of bond
    }

    //mapping issuer (address) to address of bond token (address) which acts as identifier for bonds on offer
    mapping(address => mapping (address => bond)) private issues;

    //mapping purchaser (address) to address of bond token (address) which acts as identifier for bonds subscribed
    mapping(address => mapping (address => bond)) private purchases;

    //mapping issued bond (address) to issuer (address)
    mapping(address => address) private issuers;
    
    //mapping to party => currency deposited => amount
    mapping(address => mapping(bytes32 => bytes16)) private L1deposits;
    mapping(address => mapping(bytes32 => bytes16)) private L2deposits;

    //supported ERC20 tokens for issuing Verified bond tokens
    ViaCash.token[] private tokens;
    mapping(address => uint256) private tokenIndex;

    event BondIssueRequest(address indexed issuer, bytes32 currency, uint256 amount);
    event BondIssued(address indexed issuer, bytes32 collateralCurrency, uint256 issuedAmount, address token);
    event BondRedeemed(address indexed redeemer, bytes32 redemptionCurrency, uint256 redemptionAmount, address token);
    event BondPurchased(address indexed purchaser, bytes32 paidInCurrency, uint256 purchasedAmount, address token);

    function initialize(bytes32 _name, bytes32 _currency, address _owner, address _fee, address _bridge) public initializer {
        Ownable.initialize(_owner);
        factory = ViaFactory(msg.sender);
        name = string(abi.encodePacked(_name));
        symbol = _name.substring(0,5);
        bondName = _name;
        bondCurrency = _currency;
        rates = VerifiedRates(_fee);
        bridge = _bridge;
    }

    function setSigner(address _signer) onlyOwner external{
        bridge = _signer;
    }

    //handling pay in of ether for issue of via bond tokens
    function() external payable{
        //ether paid in
        require(msg.value !=0);
        //only to pay in ether
        require(msg.data.length==0);
        // contract must not be paused
        require(paused == false);
        //check aml status
        //require(fee.amlCheck(msg.sender)==true); 
        //issue verified bond tokens
        L1deposits[msg.sender]["ether"] = ABDKMathQuad.add(L1deposits[msg.sender]["ether"], ABDKMathQuad.fromUInt(msg.value));  
    }

    /**
        Specifies list of supported tokens that can be deposited to issue Verified cash tokens
        @param  _token  address of token supported
        @param  _name   name of token supported
     */
    function supportTokens(address _token, bytes32 _name) onlyOwner external {
        if(tokenIndex[_token]==0){
            ViaCash.token memory t = ViaCash.token ({
                tokenName: _name,
                tokenAddress: _token
            });
            tokens.push(t);
            tokenIndex[_token] = tokens.length;
        }
    }

    /**
        Checks if a specified token is supported for issuing Verified cash tokens
        @param  _token  token that is supported for issuing cash tokens
     */
    function checkSupportForToken(address _token) external view returns(bool){
        if(tokenIndex[_token]>0)
            return true;
        else
            return false;
    }

    /**
        Used by external apps (eg, exchange, wallet) to buy Verified bond token 
        @param  _token  address of token used by investor to buy the bond token
        @param  _amount amount of token that is transferred from investor to this bond token issuing contract
     */
    function requestIssue(address _token, uint256 _amount, address _buyer) external {
        require(paused == false, "Contract in paused state");
        bool found = false;
        if(tokenIndex[_token]>0)
            found = true;
        if(found){
            bytes32 currencyName = tokens[tokenIndex[_token]].tokenName;
            if(IERC20(_token).transferFrom(msg.sender, address(this), _amount)){
                L2deposits[_buyer][currencyName] = ABDKMathQuad.add(L2deposits[_buyer][currencyName], ABDKMathQuad.fromUInt(_amount));
                emit BondIssueRequest(_buyer, currencyName, _amount);
            }
        }
        else if(ABDKMathQuad.cmp(L1deposits[msg.sender]["ether"], ABDKMathQuad.fromUInt(_amount))>=0){
            L1deposits[msg.sender]["ether"] = ABDKMathQuad.sub(L1deposits[msg.sender]["ether"], ABDKMathQuad.fromUInt(_amount));
            L2deposits[_buyer]["ether"] = ABDKMathQuad.add(L2deposits[_buyer]["ether"], ABDKMathQuad.fromUInt(_amount));
            emit BondIssueRequest(_buyer, "ether", _amount);                
        }
    }
    
    //adds issued Verified bond tokens to user balances
    function addIssuedBalance(uint256 amount, address issuer, bytes32 currency, address issuedBond, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                        external {
        bytes memory prefix = "\x19Ethereum Signed Message:\n32";
        bytes32 prefixedHashMessage = keccak256(abi.encodePacked(prefix, _hashedMessage));
        address signer = ecrecover(prefixedHashMessage, _v, _r, _s);
        require(signer==bridge);
        if(factory.getIssuer("ViaBond", currency)==address(this)){
            issues[issuer][issuedBond].parValue = ABDKMathQuad.fromUInt(amount);
            issues[issuer][issuedBond].paidInCurrency = currency;
            issuers[issuedBond] = issuer;
            emit BondIssued(issuer, currency, amount, issuedBond);
        }
    }

    //adds purchased Verified bond tokens to user balances   
    function purchase(uint256 amount, address purchaser, bytes32 currency, address purchasedBond, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                        external {
        bytes memory prefix = "\x19Ethereum Signed Message:\n32";
        bytes32 prefixedHashMessage = keccak256(abi.encodePacked(prefix, _hashedMessage));
        address signer = ecrecover(prefixedHashMessage, _v, _r, _s);
        require(signer==bridge);
        if(factory.getIssuer("ViaBond", currency)==address(this)){
            address issuer = issuers[purchasedBond];
            issues[issuer][purchasedBond].counterParties[issues[issuer][purchasedBond].counterParties.length] = purchaser;
            issues[issuer][purchasedBond].purchasedIssueAmount = ABDKMathQuad.add(issues[issuer][purchasedBond].purchasedIssueAmount, ABDKMathQuad.fromUInt(amount)); 
            purchases[purchaser][purchasedBond].counterParties.push(issuer);
            purchases[purchaser][purchasedBond].purchasedIssueAmount = ABDKMathQuad.add(purchases[purchaser][purchasedBond].purchasedIssueAmount, ABDKMathQuad.fromUInt(amount));
            purchases[purchaser][purchasedBond].paidInCurrency = currency;
            emit BondPurchased(purchaser, currency, amount, purchasedBond);
        }
    }
    
    //redeems issued Verified bond
    function redeemBond(uint256 amount, address payOutTo, bytes32 currency, address redeemedToken, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                        external {
        bytes memory prefix = "\x19Ethereum Signed Message:\n32";
        bytes32 prefixedHashMessage = keccak256(abi.encodePacked(prefix, _hashedMessage));
        address signer = ecrecover(prefixedHashMessage, _v, _r, _s);
        require(signer==bridge);
        if(currency=="ether")
            address(uint160(payOutTo)).transfer(amount); 
        else{
            IERC20(redeemedToken).transferFrom(address(this), payOutTo, amount);
        }
        emit BondRedeemed(payOutTo, currency, amount, redeemedToken);
    }
    
    function pause() onlyOwner public {
        _pause();
    }

    function unpause() onlyOwner public {
        _unpause();
    }
    
}

