// SPDX-License-Identifier: MIT

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

interface IBond{

    struct token{
        bytes32 tokenName;
        address tokenAddress;
    }

    function supportTokens(address _token, bytes32 _name) external;

    function checkSupportForToken(address _token) external view returns(bool);

    function getSupportedTokens() external view returns(token[] memory);

    function requestIssue(address _token, uint256 _amount, address _buyer) external;
    
    function addIssuedBalance(uint256 amount, address issuer, bytes32 currency, address issuedBond, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) external;

    function purchase(uint256 amount, address purchaser, bytes32 currency, address purchasedBond, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) external;
    
    function redeemBond(uint256 amount, address payOutTo, bytes32 currency, address redeemedToken, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) external;

    function setSigner(address _signer) external;

}
