// Verified securities manager that updates holdings of securities by users
// (c) Kallol Borah, 2021

pragma solidity 0.5.7;

import "./utilities/Pausable.sol";
import "abdk-libraries-solidity/ABDKMathQuad.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/ownership/Ownable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/ERC20.sol";

contract Security is Initializable, Ownable, Pausable {

    using ABDKMathQuad for uint256;
    using ABDKMathQuad for int256;
    using ABDKMathQuad for bytes16;

    struct securities{
        bytes16 amount;
        bytes32 currency;
    }

    //mapping token holders to isin to securities struct
    mapping(address => mapping(bytes32 => securities)) private securitiesHoldings;

    address[] public holders;

    //bridge
    address bridge;

    function initialize() public initializer{
        Ownable.initialize(msg.sender);
    }

    function setSigner(address _signer) onlyOwner external{
        bridge = _signer;
    }

    //called by L2 network to add issued security tokens to user balances
    function addToBalance(bytes32 isin, uint256 amount, address tokenHolder, bytes32 currency, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                            external {
        bytes memory prefix = "\x19Ethereum Signed Message:\n32";
        bytes32 prefixedHashMessage = keccak256(abi.encodePacked(prefix, _hashedMessage));
        address signer = ecrecover(prefixedHashMessage, _v, _r, _s);
        require(signer==bridge);
        addBalance(isin, ABDKMathQuad.fromUInt(amount), tokenHolder, currency);
    }

    //adds issued security tokens to user balances
    function addBalance(bytes32 isin, bytes16 amount, address tokenHolder, bytes32 currency) private{
        if(ABDKMathQuad.cmp(securitiesHoldings[tokenHolder][isin].amount,0)==1){
            securitiesHoldings[tokenHolder][isin].amount= ABDKMathQuad.add(securitiesHoldings[tokenHolder][isin].amount, amount);
        } else{
            securitiesHoldings[tokenHolder][isin].amount= amount;
            securitiesHoldings[tokenHolder][isin].currency= currency;
            holders.push(tokenHolder);
        }
    }

    //transfers security token balance from one user to another
    function transferBalance(bytes32 isin, address transferor, uint256 amount, address transferee, bytes32 currency, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                            external {
        bytes memory prefix = "\x19Ethereum Signed Message:\n32";
        bytes32 prefixedHashMessage = keccak256(abi.encodePacked(prefix, _hashedMessage));
        address signer = ecrecover(prefixedHashMessage, _v, _r, _s);
        require(signer==bridge);
        require(ABDKMathQuad.cmp(securitiesHoldings[transferor][isin].amount, ABDKMathQuad.fromUInt(amount))>=0);
        securitiesHoldings[transferor][isin].amount= ABDKMathQuad.sub(securitiesHoldings[transferor][isin].amount, ABDKMathQuad.fromUInt(amount));
        if(ABDKMathQuad.cmp(securitiesHoldings[transferor][isin].amount,0)==0){
            securitiesHoldings[transferor][isin].currency="";
        }
        addBalance(isin, ABDKMathQuad.fromUInt(amount), transferee, currency);
    }

}
