pragma solidity 0.8.0;

interface IDistribution {

    function getPaymentFeeCollected(bytes32 _currency) external returns(uint256);

    function getLoanFeeCollected(bytes32 _currency) external view returns(uint256);

    function addRevenueShareholder(bytes32 _type, address _shareholder, bytes32 _currency) external;

    function getRevenueShareholders(bytes32 _type, bytes32 _currency) external view returns(address[] memory shareholders);

    function shareFee() external;

    function getIssuingFeeCollected(address _platform, address _token) external view returns(uint256);

    function getTradingFeeCollected(address _platform, address _token) external returns(uint256);

    function shareCollection() external;

}
