// SPDX-License-Identifier: MIT

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

interface ViaBond {

    //a Via bond has some value, corresponds to a fiat currency
    //can have many purchasers and a issuer that have agreed to a zero coupon rate which determines the start price of the bond
    //and a tenure in unix timestamps of seconds counted from 1970-01-01. Via bonds are of one year tenure.
    struct bond{
        bytes16 parValue;               //face value of bond issued or purchased in the bond's currency        
        bytes16 paidInAmount;           //amount paid in as collateral for issue of bond or as payment for purchase of bond
        bytes32 paidInCurrency;         //currency denomination of collateral for issue or payment for purchase of bond
        uint256 timeIssuedOrSubscribed; //time of bond issue or subscription
        bytes16 purchasedIssueAmount;   //amount purchased at price of bond for paidInCurrency
        address[] counterParties;
    }

    function setSigner(address _signer) external;

    function convert(bytes32 txId, bytes16 result, bytes32 rtype) external;

    function transferForward(address _sender, address _receiver, uint256 _tokens, address _forwarder) external returns (bool);

    function requestIssueFromL1(uint256 amount, address payer, bytes32 currency, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                        external;

    function requestIssue(bytes16 amount, address payer, bytes32 currency, address cashContract) external returns(bool);

    function requestPurchase(bytes16 amount, address payer, bytes32 currency, address cashContract) external returns(bool);

    function requestRedemption(bytes16 amount, address payer, bytes32 currency, address tokenContract) external returns(bool);
    
    function getBonds() external view returns(address[] memory);

    function getBondIssues(address issuer, address bondToken) external view returns(uint256, uint256, uint256, bytes32, uint256);

    function getBondPurchases(address issuer, address bondToken) external view returns(uint256, uint256, bytes32, uint256);

}
