// Interface definition of a bond
// (c) Kallol Borah, 2021

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

interface Bond {

    struct currencySpecificBondAttribs {
        uint256 issueSize;
        uint256 coupon;
        uint256 offerPrice; 
        uint256 minAskPrice;   
        uint256 minSubscription;
        bytes32 currency;
        address security;
    }

    struct commonBondAttribs {
        bytes32 offerType;
        uint256 couponPaymentCycle; 
        uint256 tenure; //in seconds
        bytes32 isin;
        bytes32 status;
        string offeringDocuments;
    }

    function issueBond( uint256 _issueSize,
                        uint256 _offerPrice, 
                        uint256 _minAskPrice, 
                        uint256 _minSubscription,
                        uint256 _couponPaymentCycle, 
                        uint256 _tenure,
                        bytes32 _currency,
                        bytes32 _offerType,
                        bytes32 _isin,
                        string calldata _offeringDocuments
                        ) external;

    function getBond() external view returns(Bond.currencySpecificBondAttribs[] memory, Bond.commonBondAttribs memory);

    function getmaturityDate() external view returns(uint256, uint256, uint256);

    function getfirstCouponDate() external view returns(uint256, uint256, uint256);

    function computeNextCouponDate() external view returns(uint256, uint256, uint256);

    function computeNextInstallment(address _currency) external returns(uint256);

    function getcouponFrequencyInMonths() external view returns(uint256);

    function getinterestRateInBips(address _currency) external view returns(uint256);

    function getPaymentAmountFor(address _beneficiary) external view returns(uint256);
    
}
