// Interface for securities registry
// (c) Kallol Borah, 2021

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;
interface ISecuritiesRegistry {

    function createSecurity(address _issuer, address _security, bytes32 _currency, bytes32 _company, bytes32 _isin, bytes32 _settlementBy) external;

    function getToken(bytes32 _currency, bytes32 _company, bytes32 _isin) external view returns(address);

    function isIsinPresent(bytes32) external returns(bool);

    function registerCorporateAction(bytes32 _category, string calldata _action, bytes32 _isin) external;

    function registerCreditScore(bytes32 _company, bytes32 _isin, bytes32 _score) external;

    function registerPrice(bytes32 _isin, bytes16 _price) external;

    function getCorporateActions(bytes32 _isin, bytes32 _category) external view returns(string[] memory);

    function getCreditScore(bytes32 _isin, bytes32 _company) external view returns(bytes32);

    function getPrice(bytes32 _isin) external view returns(bytes16);

    function getSecuritiesInvested() external view returns(address[] memory);
    
}
