// Issue of Verified financial products
// Offer issues to liquidity pools for IoIs, fix pricing, make allotments, draw down capital, create subscriptions
// (c) Kallol Borah, Verified Network, 2021

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

import "./interfaces/ViaCash.sol";
import "./interfaces/ViaFactory.sol";
import "./interfaces/VerifiedIssues.sol";
import "./interfaces/VerifiedProducts.sol";
import "./interfaces/ISecurity.sol";
import "./interfaces/ISecuritiesRegistry.sol";
import "./interfaces/IOrderPool.sol";
import "./interfaces/IPoolFactory.sol";
import "./utilities/BokkyPooBahsDateTimeLibrary.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";
import "@openzeppelin/contracts/ownership/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Issues is VerifiedIssues, Initializable, Ownable {

    using BokkyPooBahsDateTimeLibrary for uint;

    bytes32 productRef;
    bytes32 category;
    address productOwner;
    address issuer;
    address registrar;
    address security;
    address issue;
    address productContract;
    address securityContract;
    address securityRegistriesContract;
    address viaFactoryContract;
    address poolFactoryContract;
    uint256 timeOfIssue;    

    address[] platforms;

    address[] currencies;
    mapping(address => uint256) currencyIndex;
 
    address[] investors;

    address[] beneficiaries;

    //bridge
    address bridge;

    //mapping of currencies to investors in pools 
    mapping(bytes32 => ioi[]) subscribers;

    //mapping of beneficiaries to payouts
    mapping(address => VerifiedIssues.payment[]) payouts;

    //mapping of market making platform to liquidity providers
    mapping(address => IMarketMaker.token[]) liquidityproviders;

    //mapping of platform to security token issued to pools
    mapping(address => mapping(address => bytes32[])) poolsOnClose;
    mapping(address => mapping(address => bool)) issueCloseStatus;

    //mapping of platform to pool to subscribers
    mapping(address => mapping(bytes32 => IMarketMaker.subscriptions[])) subscribersOnClose;

    event issued (
        uint256 issueSize,
        uint256 offerPrice, 
        uint256 minAskPrice,   
        uint256 minSubscription,
        bytes32 currency,
        address security,
        bytes32 offerType,
        bytes32 isin,
        bytes32 status,
        bytes32 productCategory
    );

    event offered(address security, bytes32 status);

    event opened(address security, bytes32 status);

    event closed(address security, bytes32 status);

    event allotted(address security, bytes32 status);

    event offer(address platform, address security, bytes32 isin, uint256 issueSize, address currency, uint256 offerPrice, uint256 minAskPrice, uint256 minSubscription, address issuer);

    event start(address platform, address security, uint256 cutOffTime, address issuer);

    event close(address platform, address security, address issuer);

    event subscriptions(address platform, bytes32 pool, address issuer);

    event accept(address platform, bytes32 pool, address investor, uint256 amount, address asset, address issuer);

    event settle(address platform, bytes32 pool, address issuer);

    event reject(address platform, bytes32 pool, address investor, address issuer);

    event getLP(address platform, address security, address issuer);

    modifier onlyDPorIssuer(address sender){
        require(registrar==sender || issuer==sender);
        _;
    }

    function initialize(bytes32 _productReference, 
                        bytes32 _productCategory, 
                        address _issuer, 
                        address _registrar, 
                        address _owner,
                        address _securityContract, 
                        address _securityRegistriesContract,
                        address _viaFactoryContract,
                        address _poolFactoryContract,
                        address _bridge
                        ) public initializer{
        productOwner = _owner;
        productRef = _productReference;
        category = _productCategory;
        issuer = _issuer;
        registrar = _registrar;
        productContract = msg.sender;
        securityContract = _securityContract;
        securityRegistriesContract = _securityRegistriesContract;
        viaFactoryContract = _viaFactoryContract;
        poolFactoryContract = _poolFactoryContract;
        bridge = _bridge;
        address[] memory platformsRegistered =  VerifiedProducts(productContract).getPlatforms();
        for(uint256 i=0; i<platformsRegistered.length; i++)
            platforms.push(platformsRegistered[i]);
    }

    function setSigner(address _signer) onlyOwner external{
        bridge = _signer;
    }

    function getOwner() external view returns(address){
        return productOwner;
    }

    function registerPlatform(address _liquidityPlatform) external {
        require(productContract==msg.sender);
        platforms.push(_liquidityPlatform);
    }    

    function getLiquidityProviders(address platform) external returns(IMarketMaker.token[] memory){
        //return IMarketMaker(platform).getLiquidityProviders(security);
        emit getLP(platform, security, address(this));
        return (liquidityproviders[platform]);
    }   

    function setLiquidityProviders(address platform, IMarketMaker.token[] calldata lps, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                        external {
        bytes32 payloadHash = keccak256(abi.encode(_hashedMessage, "L2toL1"));
        bytes32 messageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", payloadHash));
        require(ecrecover(messageHash, _v, _r, _s)== bridge);
        for(uint i=0; i<lps.length; i++){
            IMarketMaker.token memory lp = IMarketMaker.token({
                 owner : lps[i].owner,
                 amountOffered : lps[i].amountOffered,
                 amountDesired : lps[i].amountDesired,
                 min : lps[i].min,
                 isin : lps[i].isin
            }); 
            liquidityproviders[platform].push(lp);
        }
    }

    function setPlatformPools(address platform, address securityToken, bytes32[] calldata pools, bool status, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                        external {
        bytes32 payloadHash = keccak256(abi.encode(_hashedMessage, "L2toL1"));
        bytes32 messageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", payloadHash));
        require(ecrecover(messageHash, _v, _r, _s)== bridge);
        issueCloseStatus[platform][securityToken]=status;
        poolsOnClose[platform][securityToken]=pools;
    }

    function setPlatformSubscribers(address platform, bytes32 pool, IMarketMaker.subscriptions[] calldata eois, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                        external {
        bytes32 payloadHash = keccak256(abi.encode(_hashedMessage, "L2toL1"));
        bytes32 messageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", payloadHash));
        require(ecrecover(messageHash, _v, _r, _s)== bridge);
         for(uint i=0; i<eois.length; i++){
            IMarketMaker.subscriptions memory s = IMarketMaker.subscriptions({
                 investor : eois[i].investor,
                 asset : eois[i].asset,
                 name : eois[i].name,
                 amount : eois[i].amount,
                 price : eois[i].price
            }); 
            subscribersOnClose[platform][pool].push(s);
         }
    }

    function getBeneficiaries() external onlyDPorIssuer(msg.sender) view returns(address[] memory){
        return beneficiaries;
    }

    function getPaymentStatusFor(address _beneficiary) external onlyDPorIssuer(msg.sender) returns(VerifiedIssues.payment[] memory){
        return payouts[_beneficiary];
    }

    function isAllPaidFor(uint256 _byTime) external onlyDPorIssuer(msg.sender) returns(address[] memory){
        address[] memory unpaidBeneficiaries = new address[](beneficiaries.length);
        uint count =0;
        for(uint i=0; i<beneficiaries.length; i++){
            uint latestPayment = payouts[beneficiaries[i]].length-1;
            if(_byTime > payouts[beneficiaries[i]][latestPayment].payoutDate){
                unpaidBeneficiaries[count] = beneficiaries[i];
                count = count + 1;
            }
        }
        return unpaidBeneficiaries;
    }

    function getDateOfIssue() external onlyDPorIssuer(msg.sender) returns(uint256 day, uint256 month, uint256 year){
        return (BokkyPooBahsDateTimeLibrary.getDay(timeOfIssue),
                BokkyPooBahsDateTimeLibrary.getMonth(timeOfIssue),
                BokkyPooBahsDateTimeLibrary.getYear(timeOfIssue));
    }

    function payOut(address _beneficiary, bytes32 _currency, uint256 _amount) external onlyDPorIssuer(msg.sender){
        (address token, bytes32 tokentype) = ViaFactory(viaFactoryContract).getAddressAndType(_currency);
        if(tokentype=="ViaCash"){
            if(ViaCash(token).transferFrom(msg.sender, _beneficiary, _amount)){
                VerifiedIssues.payment memory paid = VerifiedIssues.payment({
                    currency : _currency,
                    amount : _amount,
                    payoutDate : now,
                    payer : msg.sender
                });
                payouts[_beneficiary][payouts[_beneficiary].length] = paid;                
            }
        }
    }

}
