// Verified financial products
// Define products, accepts issuing documentation for registrar, create issues of products
// (c) Kallol Borah, Verified Network, 2021

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

import "./interfaces/VerifiedKYC.sol";
import "./interfaces/VerifiedClient.sol";
import "./interfaces/VerifiedIssues.sol";
import "./interfaces/VerifiedProducts.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";
import "@openzeppelin/upgrades/contracts/upgradeability/ProxyFactory.sol";

contract Products is VerifiedProducts, ProxyFactory, Initializable {

    //mapping of product registration ref to issue
    mapping(bytes32 => address) private issues;

    //mapping of registration reference to registration record
    mapping(bytes32 => product) private registrations;

    //array of registration requests
    bytes32[] regRefs;

    address kyc;
    address client;
    address issueContract;
    address securityContract;
    address securityRegistriesContract;
    address viaFactoryContract;
    address owner;

    event ProductIssued(address _issue, bytes32 _ref);

    function initialize(address _client, address _kyc, address _issueContract, address _securityContract, address _securityRegistriesContract, address _viaFactoryContract) public initializer{
        client = _client;
        kyc = _kyc;
        issueContract = _issueContract;
        securityContract = _securityContract;
        securityRegistriesContract = _securityRegistriesContract;
        viaFactoryContract = _viaFactoryContract;
        owner = msg.sender;
    }

    function recordProduct( bytes32 _productCategory,
                            bytes32 _issuerName,
                            bytes32 _issuerAddress,
                            bytes32 _issuerCountry,
                            bytes32 _issuerSignatoryEmail,
                            bytes32 _arrangerName,
                            bytes32 _arrangerAddress,
                            bytes32 _arrangerCountry,
                            bytes32 _arrangerSignatoryEmail,
                            string calldata _registrationDocuments
                            ) external{
        require(VerifiedKYC(kyc).getStatus(msg.sender)==3);
        require(_productCategory=="Stock" || _productCategory=="Bond");
        bytes32 ref = keccak256(abi.encodePacked(msg.sender, now));
        regRefs.push(ref);
        registrations[ref].productCategory = _productCategory;
        registrations[ref].issuerName = _issuerName;
        registrations[ref].issuerAddress = _issuerAddress;
        registrations[ref].issuerCountry = _issuerCountry;
        registrations[ref].issuerSignatoryEmail = _issuerSignatoryEmail;
        registrations[ref].arrangerName = _arrangerName;
        registrations[ref].arrangerAddress = _arrangerAddress;
        registrations[ref].arrangerCountry = _arrangerCountry;
        registrations[ref].arrangerSignatoryEmail = _arrangerSignatoryEmail;
        registrations[ref].registrationDocuments = _registrationDocuments;
        registrations[ref].issuer = msg.sender;
    }

    function getProductsForClient() external view returns(bytes32[] memory){
        require(VerifiedKYC(kyc).getStatus(msg.sender)==3);
        uint size = regRefs.length;
        bytes32[] memory resp = new bytes32[](size);
        uint count = 0;
        for(uint i=0; i<size; i++){
            if(registrations[regRefs[i]].issuer == msg.sender){
                resp[count] = regRefs[i];
                count = count + 1;
            }
        }
        return resp;
    }

    function getProductsForCountry(bytes32 _country) external view returns(bytes32[] memory){
        require(VerifiedKYC(kyc).getStatus(msg.sender)==3);
        address[] memory managers = VerifiedClient(client).getManagers("DP", _country);
        for(uint i=0; i<managers.length; i++){
            if(managers[i]==msg.sender){
                uint size = regRefs.length;
                bytes32[] memory resp = new bytes32[](size);
                uint count = 0;
                for(i=0; i<size; i++){
                    if(registrations[regRefs[i]].issuerCountry == _country){
                        resp[count] = regRefs[i];
                        count = count + 1;
                    }
                }
                return resp;
            }
        }
    }

    function getProduct(bytes32 ref) external view returns(VerifiedProducts.product memory){
        require(VerifiedKYC(kyc).getStatus(msg.sender)==3);
        product memory security = product ({
            productCategory : registrations[ref].productCategory,
            issuerName : registrations[ref].issuerName,
            issuerAddress : registrations[ref].issuerAddress,
            issuerCountry : registrations[ref].issuerCountry,
            issuerSignatoryEmail : registrations[ref].issuerSignatoryEmail,
            arrangerName : registrations[ref].arrangerName,
            arrangerAddress : registrations[ref].arrangerAddress,
            arrangerCountry : registrations[ref].arrangerCountry,
            arrangerSignatoryEmail : registrations[ref].arrangerSignatoryEmail,
            issue : registrations[ref].issue,
            issuer : registrations[ref].issuer,
            issuerRegistrationCertificate : registrations[ref].issuerRegistrationCertificate,
            arrangerRegistrationCertificate : registrations[ref].arrangerRegistrationCertificate,
            registrationDocuments : registrations[ref].registrationDocuments
        });
        return security;
    }

    function registerCertificate(bytes32 _ref, string calldata _issuerCertificate, string calldata _arrangerCertificate) external {
        require(VerifiedKYC(kyc).getStatus(msg.sender)==3);
        if(registrations[_ref].issuer==msg.sender){
            registrations[_ref].issuerRegistrationCertificate = _issuerCertificate;
            registrations[_ref].arrangerRegistrationCertificate = _arrangerCertificate;
        }
    }

    function getCertificate(bytes32 _ref) external view returns(string memory, string memory){
        require(VerifiedKYC(kyc).getStatus(msg.sender)==3);
        require(VerifiedClient(client).getRole(msg.sender)=="DP");
        return (registrations[_ref].issuerRegistrationCertificate, registrations[_ref].arrangerRegistrationCertificate);
    }

    function confirmProduct(bytes32 _ref) external {
        require(VerifiedKYC(kyc).getStatus(msg.sender)==3);
        require(VerifiedClient(client).getRole(msg.sender)=="DP");

        bytes memory _payload = abi.encodeWithSignature("initialize(bytes32,bytes32,address,address,address,address,address,address)", _ref, 
                                registrations[_ref].productCategory, registrations[_ref].issuer, msg.sender, owner, securityContract, securityRegistriesContract, viaFactoryContract);

        //create issue
        address _issue = deployMinimal(issueContract, _payload);
        emit ProductIssued(_issue, _ref);
         
        //map issue to product registration reference
        issues[_ref] = _issue;

    }

    function getIssue(bytes32 _ref) external view returns(address){
        return issues[_ref];
    }

    function getIssuerName(bytes32 _ref) external view returns(bytes32){
        return registrations[_ref].issuerName;
    }

    function getIssuerAddress(bytes32 _ref) external view returns(address){
        return registrations[_ref].issuer;
    }

    function registerPlatform(address _liquidityPlatform, address _owner) external {
        require(owner==_owner);
        for(uint i=0; i<regRefs.length; i++){
            VerifiedIssues(issues[regRefs[i]]).registerPlatform(_liquidityPlatform);
        }
    }

    function checkIssue(address _issuer) external view returns(bool){
        bool found = false;
        for(uint i=0; i<regRefs.length; i++){
            if(issues[regRefs[i]]==_issuer)
                found = true;
        }
        if(found)
            return true;
        else
            return false;
    }

}
