// Interface definition of a Share (stock)
// (c) Kallol Borah, 2021

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

interface Share {

    struct currencySpecificStockAttribs {
        uint256 issueSize;
        uint256 faceValue;
        uint256 offerPrice; 
        uint256 minAskPrice;   
        uint256 minSubscription;
        bytes32 currency;
        address security;
    }

    struct commonStockAttribs {
        bytes32 offerType;
        bytes32 isin;
        bytes32 status;
        string offeringDocuments;
    }

    function issueShare(uint256 _issueSize,
                        uint256 _offerPrice, 
                        uint256 _minAskPrice, 
                        uint256 _minSubscription,
                        bytes32 _currency,
                        bytes32 _offerType,
                        bytes32 _isin,
                        string calldata _offeringDocuments
                        ) external;

    function getShare() external view returns(Share.currencySpecificStockAttribs[] memory, Share.commonStockAttribs memory);

}
