// Interface of Verified product issues
// (c) Kallol Borah, 2021

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

import "./IMarketMaker.sol";

interface VerifiedIssues {

    struct payment {
        bytes32 currency;
        uint256 amount;
        uint256 payoutDate;
        address payer;
    }

    struct ioi{
        bytes32 currency;
        uint256 amount;
        uint256 price;
        bytes32 poolid;
        bool status;
        address platform;
        address investor;
        address asset;
    }

    function setSigner(address _signer) external;

    function registerPlatform(address _liquidityPlatform) external;

    function askOffers() external;

    //function getLiquidityProviders(address platform) external returns(IMarketMaker.token[] memory);

    function startIssue(uint256 cutOffTime) external;

    function getSubscribers() external returns(VerifiedIssues.ioi[] memory);

    function allotIssue(bytes32 _allotment, address _platform, bytes32 _pool, address _investor, uint256 _amount, address _asset) external;

    function getBeneficiaries() external view returns(address[] memory);
    
    function getPaymentStatusFor(address _beneficiary) external returns(VerifiedIssues.payment[] memory);

    function isAllPaidFor(uint256 _byTime) external returns(address[] memory);

    function getDateOfIssue() external returns(uint256, uint256, uint256);
    
    function payOut(address _beneficiary, bytes32 _currency, uint256 _amount) external;

    function getOwner() external view returns(address);

    //function setLiquidityProviders(address platform, IMarketMaker.token[] calldata lps, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) external;

    function setPlatformPools(address platform, address security, bytes32[] calldata pools, bool status, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) external;

    function setPlatformSubscribers(address platform, bytes32 pool, IMarketMaker.subscriptions[] calldata eois, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) external;
    
    function settleIssue(address _issue) external;
}
