pragma solidity 0.5.7;

interface IOrderPool {

    /*enum OrderType{ Market, Limit, StopLoss, StopLossMarket, StopLossLimit }

    enum OrderStatus{ Filled, PartlyFilled, Open, Cancelled, Expired }
    
    enum Order{ Buy, Sell } 

    enum Status{ Halt, Open }*/

    function newOrder(  address _security, 
                        address _cash, 
                        uint256 _price,
                        uint256 _trigger, 
                        uint256 _qty, 
                        bytes32 _otype, 
                        bytes32 _order) external returns(bytes32);

    function cancelOrder(bytes32 ref) external returns(bool);

    function editOrder( bytes32 ref,
                        uint256 _price,
                        uint256 _trigger, 
                        uint256 _qty, 
                        bytes32 _otype, 
                        bytes32 _order) external returns(bool);

    function getSecurityStatus() external view returns(bool);

    function setSecurityStatus(bool _status) external;

    function revertTrade(bytes32 _orderRef, uint256 _qty, bytes32 _order) external;

}
