// Interface for post trade settlements
// (c) Kallol Borah, 2021

pragma solidity 0.5.7;

interface IPostTrade {

    //enum SettlementStatus { Confirm, Reject, Pending }

    enum FeeType {  Regulatory, 
                    Tax,
                    Commission,
                    ExchangeFees,
                    Stamp,
                    Levy,
                    Other,
                    Markup,
                    ConsumptionTax,
                    Transaction, 
                    Conversion,
                    Agent,
                    TransferFee,
                    SecurityLending,
                    TradeReporting, 
                    TaxPrincipalAmount,
                    TaxAccruedInterest,
                    NewIssuanceFee,
                    ServiceFee,
                    VAT,
                    GST
                     }

    function postSettlement( bytes32[] calldata a, uint[] calldata b, address[] calldata ) external;

    function getSettlementRequests(bytes32 _countryCode, bytes32 dpid) external view returns(bytes32[] memory);

    function getSettlementRequest(bytes32 ref) external view returns(   address[] memory, 
                                                                        bytes32[] memory,
                                                                        uint256[] memory,
                                                                        bytes16 );

    function setSettlementStatus(bytes32 ref, bytes32 _countryCode, bytes32 status) external;

}
