// Interface for post trade settlements
// (c) Kallol Borah, 2021

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;
interface IPostTrade {

    //enum SettlementStatus { Confirm, Reject, Pending }

    enum FeeType {  Regulatory, 
                    Tax,
                    Commission,
                    ExchangeFees,
                    Stamp,
                    Levy,
                    Other,
                    Markup,
                    ConsumptionTax,
                    Transaction, 
                    Conversion,
                    Agent,
                    TransferFee,
                    SecurityLending,
                    TradeReporting, 
                    TaxPrincipalAmount,
                    TaxAccruedInterest,
                    NewIssuanceFee,
                    ServiceFee,
                    VAT,
                    GST
                     }
    
    struct settlement{  address transferor;
                        address transferee;
                        address security;
                        bytes32 securityName;
                        bytes32 status;
                        bytes32 transferorDPID;
                        bytes32 transfereeDPID;
                        bytes32 isin;
                        bytes32 company;
                        bytes32 currency;
                        uint256 price;
                        uint256 consideration;
                        uint256 unitsToTransfer;
                        uint256 executionDate;
                        address orderPool;
                        bytes32 partyRef;
                        bytes32 counterpartyRef;
                    }

    function postSettlement(settlement calldata newTrade, bytes32 ref) external;

    function getSettlementRequests(bytes32 dpid) external view returns(bytes32[] memory);

    function getSettlementRequest(bytes32 ref) external view returns(  settlement memory );

    function setSettlementStatus(bytes32 ref, bytes32 status) external;

}
