// Interface for pre trade
// (c) Kallol Borah, 2021

pragma solidity 0.5.7;

interface IPreTrade {

    /*enum SecurityType { Free, LockedIn }

    enum InstrumentType { Equity, Bond, Debentures, Units, Certificates }

    enum Status { Confirmed, Rejected, Pending }*/

    function registerDematAccount(bytes32 _currencyCode) external;

    function getRegistrationRequests(bytes32 _countryCode) external view returns(bytes32[] memory);

    function getRegistrationRequest(bytes32 _ref) external view returns( address user,
                                                                        bytes32 countryCode,
                                                                        bytes32 dematAccountNo,
                                                                        bytes32 DPID,
                                                                        uint registrationDate);

    function setRegistrationStatus(bytes32 _ref, bytes32 _DPID, bytes32 _dematAccountNo) external;

    function registerSecurities( bytes32 _currencyCode, 
                                bytes32 _stype, 
                                bytes32 _isin, 
                                bytes32 _company, 
                                bytes32 _itype,
                                //bytes32 _folio,
                                //bytes32 _fromCertificateNo,
                                //bytes32 _toCertificateNo,
                                //bytes32 _fromDistinctiveNo,
                                //bytes32 _toDistinctiveNo,
                                uint _noOfCertificates,
                                uint _faceValue,
                                bytes32 _lockInReason,
                                uint _lockInReleaseDate) external returns(bytes32);

    function getConfirmationRequests(bytes32 _countryCode) external view returns(bytes32[] memory);

    function getConfirmationRequest(bytes32 _ref) external view returns (bytes32[] memory, uint[] memory, address);

    function confirmSecurities(address _user, bytes32 _ref, bytes32 _status) external;

    function getSecurity(address _token) external view returns(bytes32, bytes32, address);

    function getDP(address _party) external view returns(bytes32);
    
}
