// Interface for trade
// (c) Kallol Borah, 2021

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;
interface ITrade {

    /*enum DataType{ Bid, Offer, Trade, OpeningPrice }

    enum ExecutionStatus { Fill, PartialFill }

    enum SettlementType { STP, DP }*/

    struct order{
        address party;
        address security;
        uint256 price;
        uint256 trigger; 
        bytes32 otype;
        bytes32 order;
        bytes32 status;
        bytes32 currency;
        bytes32 securityName;
        uint256 qty;
        uint256 dt;
    }

    struct trade{
        address transferor;
        address transferee;
        address security;
        uint256 price;
        bytes32 currency;
        bytes32 order;
        bytes32 stype;
        uint256 qty;
        uint dt;
    }

    function reportTrade(trade calldata newTrade, bytes32 partyRef, bytes32 counterpartyRef) external;

    function getTrade(bytes32 ref) external view returns(uint256, uint256);

    function reportOrder(order calldata newOrder, bytes32 ref) external;

    function editOrder(order calldata currentOrder, bytes32 ref) external;

    function cancelOrder(bytes32 _ref, bytes32 _status) external;

    function getOrders(bool originator) external view returns(bytes32[] memory);

    function getOrder(bytes32 ref) external view returns(order memory);
}
