// Creates order pools
// (c) Kallol Borah, 2021

pragma solidity 0.5.7;

import "@openzeppelin/upgrades/contracts/Initializable.sol";
import "@openzeppelin/upgrades/contracts/upgradeability/ProxyFactory.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/IERC20.sol";
import "../interfaces/IPoolFactory.sol";
import "../interfaces/ViaFactory.sol";

contract PoolFactory is Initializable, ProxyFactory, IPoolFactory{

    //mapping of security to cash to pair
    mapping(address => mapping(address => address)) private pool;

    //indexing pairs
    mapping(address => bool) private pairs;

    //event signalling creation of a trading pair
    event PoolCreated(address indexed pool, address security, address cash);

    address private via;

    address _pool;
    address _trade;
    address _posttrade;
    address _registry;

    function initialize(address _viaFactory, address _orderPool, address _tradeContract, address _posttradeContract, address _securitiesRegistry) public initializer{
        via = _viaFactory;
        _pool = _orderPool;
        _trade = _tradeContract;
        _posttrade = _posttradeContract;
        _registry = _securitiesRegistry;
    }

    function getPool(address _security, address _cash) external view returns(address){
        return pool[_security][_cash];
    }

    function confirmPool(address _pair) external view returns(bool){
        require(msg.sender==_trade);
        if(pairs[_pair]==true)
            return true;
        else
            return false;
    }

    function createPool(address _security, address _cash, bytes32 _currency, bytes32 _settlementType) external {
        require(msg.sender==_registry);
        require(_security!=_cash);
        require(ViaFactory(via).getTokenByNameType(_currency, "ViaCash")==_cash);

        if(pool[_security][_cash]==address(0x0)){
            bytes memory _payload = abi.encodeWithSignature("initialize(address,address,address,address,bytes32,address)", _security, _cash, _trade, _posttrade, _settlementType,via);

            address _pair = deployMinimal(_pool, _payload);
            emit PoolCreated(_pair, _security, _cash);
            
            pairs[_pair]=true;
            pool[_security][_cash]=_pair;
        }
    }
    
}
