// Settlement of trades
// (c) Kallol Borah, 2021 

pragma solidity 0.5.7;

import "./interfaces/IPostTrade.sol";
import "./interfaces/IOrderPool.sol";
import "./interfaces/VerifiedClient.sol";
import "./registry/Security.sol";
import "abdk-libraries-solidity/ABDKMathQuad.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/ownership/Ownable.sol";
 
contract PostTrade is IPostTrade, Initializable, Ownable{

    using ABDKMathQuad for uint256;
    using ABDKMathQuad for int256;
    using ABDKMathQuad for bytes16;

    struct settlement{      address transferor;
                            address transferee;
                            address security;
                            bytes32 securityName;
                            bytes32 status;
                            bytes32 transferorDPID;
                            bytes32 transfereeDPID;
                            bytes32 isin;
                            bytes32 company;
                            bytes32 currency;
                            bytes16 consideration;
                            uint256 unitsToTransfer;
                            uint executionDate;
    }

    mapping(bytes32 => settlement) settlements;

    bytes32[] trades;

    address client;
    address trade;

    function initialize (address _client, address _trade) public initializer{
        Ownable.initialize(msg.sender);
        client = _client;
        trade = _trade;
    }
    
    function postSettlement(bytes32[] calldata a, uint[] calldata b, address[] calldata c ) external {
        require(trade==msg.sender);
        trades.push(a[4]);
        settlements[a[4]].transferor = c[0];
        settlements[a[4]].transferee = c[1];
        settlements[a[4]].security = c[2];
        settlements[a[4]].securityName = a[6];
        settlements[a[4]].isin = a[0];
        settlements[a[4]].company = a[1];
        settlements[a[4]].consideration = ABDKMathQuad.mul(ABDKMathQuad.fromUInt(b[1]), ABDKMathQuad.fromUInt(b[0]));
        settlements[a[4]].currency = a[5];
        settlements[a[4]].unitsToTransfer = b[0];
        settlements[a[4]].executionDate = now;
        settlements[a[4]].transferorDPID = a[2];
        settlements[a[4]].transfereeDPID = a[3];
        settlements[a[4]].status = "Pending";

    }

    function getSettlementRequests(bytes32 _countryCode, bytes32 dpid) external view returns(bytes32[] memory){
        uint entries = trades.length;
        bytes32[] memory result = new bytes32[](entries);
        uint entry=0;
        bytes32 role = VerifiedClient(client).getRole(msg.sender, _countryCode);
        if(role=="DP"){
            for(uint i=0; i<trades.length; i++){
                if((settlements[trades[i]].transferorDPID==dpid ||
                    settlements[trades[i]].transfereeDPID==dpid) &&
                    settlements[trades[i]].status=="Pending"){
                    result[entry]=trades[i];
                    entry=entry+1;
                }
            }
        }
        return result;
    }

    function getSettlementRequest(bytes32 ref) external view returns(  address[] memory, 
                                                                        bytes32[] memory,
                                                                        uint256[] memory,
                                                                        bytes16 ){
                                                                       
        uint[] memory b = new uint[](2);
        b[1]=settlements[ref].unitsToTransfer;
        b[2]=settlements[ref].executionDate;
        bytes32[] memory a = new bytes32[](6);  
        a[1]=settlements[ref].isin;
        a[2]=settlements[ref].company;
        a[3]=settlements[ref].transferorDPID;
        a[4]=settlements[ref].transfereeDPID;
        a[5]=settlements[ref].currency;
        a[6]=settlements[ref].securityName;
        address[] memory c = new address[](2);
        c[1]=settlements[ref].transferor;
        c[2]=settlements[ref].transferee;
        return (c, a, b, settlements[ref].consideration);
    
    }

    function setSettlementStatus(bytes32 ref, bytes32 _countryCode, bytes32 status) external {
        require(status=="Confirm" || status=="Reject");
        bytes32 role = VerifiedClient(client).getRole(msg.sender, _countryCode);
        if(role=="DP"){
            settlements[ref].status = status;
            Security(settlements[ref].security).transferFrom(settlements[ref].transferor, 
                                                            settlements[ref].transferee, 
                                                            settlements[ref].unitsToTransfer);
        }
    }

}
