// SPDX-License-Identifier: MIT

pragma solidity 0.5.7;

interface ViaBond {

    function convert(bytes32 txId, bytes16 result, bytes32 rtype) external;

    function transferForward(address _sender, address _receiver, uint256 _tokens, address _forwarder) external returns (bool);

    function requestIssue(bytes16 amount, address payer, bytes32 currency, address cashContract) external returns(bool);

    function requestPurchase(bytes16 amount, address payer, bytes32 currency, address cashContract) external returns(bool);

    function requestRedemption(bytes16 amount, address payer, bytes32 currency, address tokenContract) external returns(bool);
    
    function getBonds() external view returns(address[] memory);

    function getBondIssues(address issuer, address bond) external view returns(bytes16, bytes16, bytes32, uint256);

    function getBondPurchases(address issuer, address bond) external view returns(bytes16, bytes16, bytes32, uint256);

}
